INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of \\(\\tan \\left','\(a\)\\(\\frac{6}{{17}}\\)','\(b\)\\(\\frac{6}{{\\sqrt {13} }}\\)','\(c\)\\(\\frac{{\\sqrt {13} }}{5}\\)','\(d\)\\(\\frac{{17}}{6}\\)','4','INVERSE TRIGONOMETRIC FUNCTIONS','2','Answer = \(d\)\\(\\tan \\left[ {{{\\sin }^{ - 1}}\\left( {\\frac{3}{5}} \\right) + {{\\cos }^{- 1}}\\left( {\\frac{3}{{\\sqrt {13} }}} \\right)} \\right]\\)= \\(\\tan \\left( {{{\\tan }^{ - 1}}\\frac{3}{4} + {{\\tan }^{ - 1}}\\frac{2}{3}}\\right)\\) = \\(\\tan \\left( {{{\\tan }^{ - 1}}\\,\\frac{{\\frac{3}{4} +\\frac{2}{3}}}{{1 - \\frac{3}{4}.\\frac{2}{3}}}} \\right)\\)\\(= 1 + 1 + 1 = 3.\\)= \\(\\tan \\left[ {{{\\tan }^{ - 1}}\\frac{{17}}{{12}} \\times \\frac{{12}}{6}}\\right]\\) = \\(\\frac{{17}}{6}\\).',CURRENT_TIMESTAMP);
