INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the lines \\(y = (2 + \\sqrt 3 )x + 4\\) and \\(y = kx + 6\\)are inclined atan angle \\({60^o}\\)to each other, then the value of k will be','\(a\)1','\(b\)2','\(c\)- 1','\(d\)- 2','3','STRAIGHT LINE','2','Answer = \(c\)\\(\\frac{{k - (2 + \\sqrt 3 )}}{{1 + k(2 + \\sqrt 3 )}} = \\sqrt 3 \\) or \\(k -2 - \\sqrt 3  = \\sqrt 3  + k2\\sqrt 3  + 3k\\),\\(k = \\frac{{ - 2(1 + \\sqrt 3 )}}{{2(1 + \\sqrt 3 )}} =  - 1.\\)',CURRENT_TIMESTAMP);
