INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The inclination of the straight line passing through the point (-3, 6) andthe midpoint of the line joining the point (4, -5) and (-2, 9) is','\(a\)\\(\\pi /4\\)','\(b\)\\(\\pi /6\\)','\(c\)\\(\\pi /3\\)','\(d\)\\(3\\pi /4\\)','4','STRAIGHT LINE','2','Answer = \(d\)Midpoint of the line joining the point \\((4,\\, - 5)\\) and \\(( - 2,\\,9)\\)is \\(\\left( {\\frac{{4 - 2}}{2},\\,\\frac{{ - 5 + 9}}{2}} \\right)\\) i.e.(1, 2).\\(\\therefore{}\\)Inclination of straight line passing through point (-3,6) andmid point \\((1,\\,2)\\) is \\(m = \\frac{{2 - 6}}{{1 + 3}} = \\frac{{ - 4}}{4} =  - 1= \\tan \\theta \\) .\\(\\Rightarrow{}\\) \\(\\theta  = \\frac{{3\\pi }}{4}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the lines \\(2x - y + 3 = 0\\) and \\(x + 2y + 3 = 0\\)is','\(a\)\\({90^o}\\)','\(b\)\\({60^o}\\)','\(c\)\\({45^o}\\)','\(d\)\\({30^o}\\)','1','STRAIGHT LINE','2','Answer = \(a\)Angle between two lines is given by \\(\\tan \\theta  = \\frac{{{m_1} -{m_2}}}{{1 + {m_1}{m_2}}}\\)Given \\({m_1} = \\frac{{ - 1}}{2},\\) \\({m_2} = 2\\) \\(\\therefore \\,\\,{m_1}{m_2} = - 1\\).So the lines are perpendicular  i.e., \\(\\theta  = 90^\\circ \\).',CURRENT_TIMESTAMP);
