INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The distance between two parallel lines \\(3x + 4y - 8 = 0\\)and \\(3x + 4y - 3= 0\\), is given by  ','\(a\)4','\(b\)5','\(c\)3','\(d\)1','4','STRAIGHT LINE','2','Answer = \(d\)Let the distance of both lines are \\({p_1}\\) and \\({p_2}\\) from origin,then \\({p_1} =  - \\frac{8}{5}\\)and \\({p_2} =  - \\frac{3}{5}\\). Hence distancebetween both the lines \\( = |{p_1}\\sim {p_2}| = \\frac{5}{5} = 1\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The distance between \\(4x + 3y = 11\\) and \\(8x + 6y = 15\\), is','\(a\) \\(\\frac{7}{2}\\)','\(b\) 4','\(c\)\\(\\frac{7}{{10}}\\)','\(d\)None of these','3','STRAIGHT LINE','2','Answer = \(c\)\\(4x + 3y = 11\\)and \\(4x + 3y = \\frac{{15}}{2}\\)Therefore, \\(D = \\left| {\\,\\frac{{11 - \\frac{{15}}{2}}}{5}\\,} \\right| =\\frac{7}{{10}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The root of the equation \\({x^3} - 3x - 5 = 0\\) in the interval      (1, 2),is','\(a\)1.13','\(b\)1.98','\(c\)1.54','\(d\)No root lies in the interval (1, 2)','4','NUMERICAL METHODS','2','Answer = \(d\)Since \\(f(1) =  - 7 < 0\\) and \\(f(2) =  - 3 < 0\\) i.e., \\(f(1)\\) and\\(f(2)\\) are of same sign. Therefore no root lies in the interval (1, 2).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\(f(x) = 0\\) has repeated root\\(a \\in ({x_1},\\;{x_2})\\), if','\(a\)\\(f\\'','\(a\) < 0\\)\(b\)\\(f\\'','\(a\) > 0\\)\(c\)\\(f\\'','\(a\) = 0\\)\(d\)None of these','3','NUMERICAL METHODS','2','Answer = \(c\) Let \\(f(x) = {(x - a)^2} \\Rightarrow f\\'(x) = 2(x - a) = 0\\)at \\(x = a\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The root of the equation \\(2x - {\\log _{10}}x = 7\\) lies between','\(a\)3 and 3.5','\(b\)2 and 3','\(c\)3.5 and 4','\(d\)None of these','3','NUMERICAL METHODS','2','Answer = \(c\)Let \\(f(x) = 2x - {\\log _{10}}x - 7 = 0\\), then \\(f(3.5) =  - {\\log_{10}}3.5 < 0\\)  and \\(f(4) = 1 - {\\log _{10}}(3.5) > 0\\).So that roots of \\(f(x)\\) lie between 3.5 and 4.',CURRENT_TIMESTAMP);
