INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The root of the equation \\({x^3} - 6x + 1 = 0\\) lies in the interval','\(a\)(2, 3)','\(b\)(3, 4)','\(c\)(3, 5)','\(d\)(4, 6)','1','NUMERICAL METHODS','2','Answer = \(a\) Let \\(f(x) = {x^3} - 6x + 1,\\) since \\(f(2) =  - ve\\),\\(f(3) =  + ve,\\)therefore roots lie in (2, 3).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The positive root of the equation \\({e^x} + x - 3 = 0\\) lies in the interval','\(a\)(0, 1)','\(b\)(1, 2)','\(c\)(2, 3)','\(d\)(2, 4)','1','NUMERICAL METHODS','2','Answer = \(a\)\\(f(x) = {e^x} + x - 3\\)\\(f(0) = 1 + 0 - 3 =  - 2(ve)\\)\\(f(1) = 0.Answer = =  + ve.\\)Hence root lies in the interval (0, 1).',CURRENT_TIMESTAMP);
