INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If  \\(\\int_{}^{} {f(x)\\sin x\\cos x\\;dx = \\frac{1}{{2({b^2} - {a^2})}}\\log(f(x))}  + c \\), then  \\(f(x) =  \\)','\(a\) \\(\\frac{1}{{{a^2}{{\\sin }^2}x + {b^2}{{\\cos }^2}x}} \\)\(b\)\\(\\frac{1}{{{a^2}{{\\sin }^2}x - {b^2}{{\\cos }^2}x}} \\)\(c\) \\(\\frac{1}{{{a^2}{{\\cos }^2}x + {b^2}{{\\sin }^2}x}} \\)\(d\)\\(\\frac{1}{{{a^2}{{\\cos }^2}x - {b^2}{{\\sin }^2}x}} \\)864. \\(\\int_{}^{} {\\frac{{dx}}{{4{{\\sin }^2}x + 5{{\\cos }^2}x}} = }  \\) ','\(a\) \\({\\tan ^{ - 1}}\\left( {\\frac{{1 + {x^2}}}{x}} \\right) + c \\)\(b\) \\({\\cot ^{- 1}}\\left( {\\frac{{1 + {x^2}}}{x}} \\right) + c \\)\(c\) \\({\\tan ^{ - 1}}\\left( {\\frac{{{x^2} - 1}}{x}} \\right) + c \\)\(d\) \\({\\cot ^{- 1}}\\left( {\\frac{{{x^2} - 1}}{x}} \\right) + c \\)866. \\(\\int_{}^{} {{{(\\log x)}^2}\\;dx = }  \\)[IIT 1971, 77]','','\(b\)\\(\\frac{1}{{{a^2}{{\\sin }^2}x - {b^2}{{\\cos }^2}x}} \\)\(c\) \\(\\frac{1}{{{a^2}{{\\cos }^2}x + {b^2}{{\\sin }^2}x}} \\)\(d\)\\(\\frac{1}{{{a^2}{{\\cos }^2}x - {b^2}{{\\sin }^2}x}} \\)864. \\(\\int_{}^{} {\\frac{{dx}}{{4{{\\sin }^2}x + 5{{\\cos }^2}x}} = }  \\) \(a\) \\({\\tan ^{ - 1}}\\left( {\\frac{{1 + {x^2}}}{x}} \\right) + c \\)\(b\) \\({\\cot ^{- 1}}\\left( {\\frac{{1 + {x^2}}}{x}} \\right) + c \\)\(c\) \\({\\tan ^{ - 1}}\\left( {\\frac{{{x^2} - 1}}{x}} \\right) + c \\)\(d\) \\({\\cot ^{- 1}}\\left( {\\frac{{{x^2} - 1}}{x}} \\right) + c \\)866. \\(\\int_{}^{} {{{(\\log x)}^2}\\;dx = }  \\)[IIT 1971, 77]\(a\) \\(x{(\\log x)^2} - 2x\\log x - 2x + c \\)\(b\) \\(x{(\\log x)^2} - 2x\\log x - x + c \\)\(c\) \\(x{(\\log x)^2} - 2x\\log x + 2x + c \\)\(d\) \\(x{(\\log x)^2} - 2x\\log x + x + c \\)','1','INDEFINITE INTEGRAL','2','Answer = \(a\)Since   \\(\\int_{}^{} {f(x)\\,\\sin x\\cos x\\,dx}  = \\frac{1}{{2({b^2} -{a^2})}}\\log \\left( {f(x)} \\right) + c \\)Therefore  \\(f(x)\\sin x\\cos x\\, = \\frac{1}{{2({b^2} -{a^2})}}.\\frac{1}{{f(x)}}f\\'(x) \\)Differentiating both sides  \\(w.r.t.\\,\\,x \\)\\(\\Rightarrow 2({b^2} - {a^2})\\sin x\\cos x = \\frac{{f\\'(x)}}{{f{{(x)}^2}}}\\)\\(\\Rightarrow \\int_{}^{} {(2{b^2}\\sin x\\cos x - 2{a^2}\\sin x\\cos x)\\,dx = }\\int_{}^{} {\\frac{{f\\'(x)}}{{{{\\{ f(x)\\} }^2}}}} \\,dx\\)\\(\\Rightarrow  \\pm \\,( - {b^2}{\\cos ^2}x - {a^2}{\\sin ^2}x) =  - \\frac{1}{{f(x)}}\\)\\(\\Rightarrow f(x) =  \\pm \\frac{1}{{({a^2}{{\\sin }^2}x + {b^2}{{\\cos }^2}x)}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of  \\(\\int {\\frac{{\\sqrt {({x^2} - {a^2})} }}{x}dx}  \\) willbe','\(a\) \\(\\left( {\\frac{{{x^2}}}{2} - \\frac{1}{4}} \\right){\\sin ^{ - 1}}x +\\frac{x}{4}\\sqrt {1 - {x^2}}  + c \\)\(b\) \\(\\left( {\\frac{{{x^2}}}{2} + \\frac{1}{4}} \\right){\\sin ^{ - 1}}x +\\frac{x}{4}\\sqrt {1 - {x^2}}  + c \\)\(c\) \\(\\left( {\\frac{{{x^2}}}{2} - \\frac{1}{4}} \\right){\\sin ^{ - 1}}x -\\frac{x}{4}\\sqrt {1 - {x^2}}  + c \\)\(d\) \\(\\left( {\\frac{{{x^2}}}{2} + \\frac{1}{4}} \\right){\\sin ^{ - 1}}x -\\frac{x}{4}\\sqrt {1 - {x^2}}  + c \\)870. \\(\\int_{}^{} {\\sqrt {\\frac{{a - x}}{x}} \\;dx = }  \\)','','\(b\) \\(\\left( {\\frac{{{x^2}}}{2} + \\frac{1}{4}} \\right){\\sin ^{ - 1}}x +\\frac{x}{4}\\sqrt {1 - {x^2}}  + c \\)\(c\) \\(\\left( {\\frac{{{x^2}}}{2} - \\frac{1}{4}} \\right){\\sin ^{ - 1}}x -\\frac{x}{4}\\sqrt {1 - {x^2}}  + c \\)\(d\) \\(\\left( {\\frac{{{x^2}}}{2} + \\frac{1}{4}} \\right){\\sin ^{ - 1}}x -\\frac{x}{4}\\sqrt {1 - {x^2}}  + c \\)870. \\(\\int_{}^{} {\\sqrt {\\frac{{a - x}}{x}} \\;dx = }  \\)\(a\) \\(a\\left[ {{{\\sin }^{ - 1}}\\sqrt {\\frac{x}{a}}  + \\sqrt {\\frac{x}{a}} \\sqrt{\\frac{{a - x}}{a}} } \\right] + c \\)','\(b\) \\({\\sin ^{ - 1}}\\frac{x}{a} + \\frac{x}{a}\\sqrt {{a^2} - {x^2}}  + c \\)\(c\) \\(a\\left[ {{{\\sin }^{ - 1}}\\frac{x}{a} - \\frac{x}{a}\\sqrt {{a^2} - {x^2}} }\\right] + c \\)\(d\) \\({\\sin ^{ - 1}}\\frac{x}{a} - \\frac{x}{a}\\sqrt {{a^2} - {x^2}}  + c \\)','1','INDEFINITE INTEGRAL','2','Answer = \(a\)Let  \\(\\sqrt {({x^2} - {a^2})}  = t \\)\\(= x{(\\log x)^2} - 2x\\log x + 2x + c.\\)\\(\\Rightarrow{} \\)  \\({x^2} - {a^2} = {t^2} \\)  \\(\\Rightarrow{} \\)  \\({x^2} ={a^2} + {t^2} \\)\\(\\therefore  \\)\\(xdx = tdt\\)\\(\\therefore{} \\)  \\(\\int {\\frac{{\\sqrt {({x^2} - {a^2})} }}{x}dx}  = \\int{\\frac{{\\sqrt {({x^2} - {a^2})} \\,x}}{{{x^2}}}