INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The vapour pressure of a solvent decreased by \\(10mm\\) of mercury, when anon-volatile solute was added to the solvent. The mole fraction of the solute inthe solution is 0.2. What should be the mole fraction of the solvent, if decreasein the vapour pressure is to be \\(20\\,mm\\) of mercury','(a)0.8','(b)0.6','(c)0.4','(d)0.2','','Solution-Final','2','Answer = (b)\\(\\therefore{}\\)\\({P^0} - {P_s} = {P^0} \\times {\\rm{mole}}\\,{\\rm{fraction}}\\,{\\rm{ solute}}\\)\\(10 = {P^0} \\times 0.2\\); \\(20 = {P^0} \\times n\\) \\(\\Rightarrow{}\\) \\(n = 0.4\\)\\(\\therefore{}\\) \\(N = 0.6\\).',CURRENT_TIMESTAMP);
