INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A solution contains non-volatile solute of molecular mass \\({M_p}\\). Whichof the following can be used to calculate molecular mass of the solute in termsof osmotic pressure (m = Mass of solute, V = Volume of solution and  \\(\\pi{}\\) =Osmotic pressure)  ','(a)\\(Mp = \\left( {\\frac{m}{\\pi }} \\right)\\,VRT\\)','(b)\\(Mp = \\left( {\\frac{m}{V}}\\right)\\frac{{RT}}{\\pi }\\)','(c)\\(Mp = \\left( {\\frac{m}{V}} \\right)\\frac{\\pi }{{RT}}\\)','(d)\\(Mp = \\left({\\frac{m}{V}} \\right)\\,\\pi RT\\)','2','Solution-Final','2','Answer =  (b)\\(\\pi  = \\frac{n}{V}RT\\)\\( \\Rightarrow {M_P} = \\left( {\\frac{m}{V}}\\right)\\frac{{RT}}{\\pi }\\)',CURRENT_TIMESTAMP);
