INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','False statement is ','(a)Chloroform is heavier than water','(b)\\(CC{l_4}\\) is non-inflammable','(c)Vinyl chloride is more reactive than allyl chloride','(d)\\(B{r^ - }\\) is a good nucleophile as compared to \\({I^ - }\\)','','Hydrocarbons','2','Answer =  (c,d) Vinyl chloride is less reactive than allyl chloride due to resonanceeffect.Order of nucleophilicity amongst the halide ion are as \\({I^ - } > B{r^ - } >C{l^ - }\\).',CURRENT_TIMESTAMP);
