INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The reaction, \\(C{H_3}Br + Na \\to {\\rm{Product}}\\), is called','(a)Perkin reaction','(b)Levit reaction','(c)Wurtz reaction','(d)Aldol condensation','','Hydrocarbons','2','Answer = (c) It is a common method to prepare alkanes. Methane cannot be prepared byWurtz reaction.\\(C{H_3}Br + 2Na + BrC{H_3} \\to \\mathop {{C_2}{H_6}}\\limits_{{\\rm{ethane}}}  +2NaBr\\)',CURRENT_TIMESTAMP);
