INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In the following reaction X is\\(C{H_3}N{H_2} + X + KOH \\to C{H_3}NC\\,\\,{\\rm{(highly offensive odour)}}\\)','(a)\\(C{H_2}C{l_2}\\)','(b)\\(CHC{l_3}\\)','(c)\\(C{H_3}Cl\\)','(d)\\(CC{l_4}\\)','2','Hydrocarbons','2','Answer =  (b)\\(C{H_3}N{H_2} + \\mathop {CHC{l_3}}\\limits_{(X)}  + 3KOH \\to 3KCl +C{H_3}NC + 3{H_2}O\\)',CURRENT_TIMESTAMP);
