INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following is boiled with ethyl chloride to form ethylalcohol','(a)Alcoholic KOH','(b)Aqueous KOH','(c)\\({H_2}O\\)','(d)\\({H_2}{O_2}\\)','2','Hydrocarbons','2','Answer =  (b)\\({C_2}{H_5}Cl + \\mathop {KOH}\\limits_{(aq)}  \\to {C_2}{H_5}OH + KCl\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Alkyl halide can be converted into alkene by','(a)Nucleophilic substitution reaction','(b)Elimination reaction','(c)Both nucleophilic substitution and elimination reaction','(d)Rearrangement','','Hydrocarbons','2','Answer = (b)Alkyl halide is best converted to alkene by mean of elimination reactionin form of dehydrohalogenation.\\(R - C{H_2} - C{H_2} - XR - CH = C{H_2}\\)',CURRENT_TIMESTAMP);
