INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The solubility product of a salt having general formula \\(M{X_2},\\) in wateris : \\(4 \\times {10^{ - 12}}\\). The concentration of \\({M^{2 + }}\\) ions in theaqueous solution of the salt is ','(a)\\(2.0 \\times {10^{ - 6}}M\\)','(b)\\(1.0 \\times {10^{ - 4}}M\\)','(c)\\(1.6 \\times {10^{ - 4}}M\\)','(d)\\(4.0 \\times {10^{ - 10}}M\\)','2','Ionic Equlibrium-Final_final','2','Answer =  (b)\\(M{X_2}\\) \\(\\rightleftharpoons{}\\) \\(\\mathop {{M^{2 +}}}\\limits_{S\\,\\,\\,\\,\\,\\,}  + \\mathop {2{X^ - }}\\limits_{2S\\,\\,\\,} \\)\\({K_{sp}} = {(2S)^2}(S) = 4{S^3}\\)\\(\\Rightarrow{}\\)  \\(S = 2\\,\\,\\sqrt[3]{{\\frac{{{K_{sp}}}}{4}}} =\\sqrt[3]{{\\frac{{4 \\times {{10}^{ - 12}}}}{4}}} = 1.0 \\times {10^{ - 4}}M\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','If the solubility product \\({K_{sp}}\\) of a sparingly soluble salt\\(M{X_2}\\) at \\(25^\\circ C\\) is \\(1.0 \\times {10^{ - 11}}\\), the solubility ofthe salt in mole litre\\(^{-1}\\) at this temperature will be','(a)\\(2.46 \\times {10^{14}}\\)','(b)\\(1.36 \\times {10^{ - 4}}\\)','(c)\\(2.60 \\times {10^{ - 7}}\\)','(d)\\(1.20 \\times {10^{ - 10}}\\)','2','Ionic Equlibrium-Final_final','2','Answer =  (b)\\(M{X_2}\\)\\(\\rightleftharpoons{}\\) \\(\\mathop {{M^ +}}\\limits_{(S)\\,\\,\\,}  + \\mathop {2{X^ - }}\\limits_{{{(2S)}^2}\\,\\,} \\);\\({K_{sp}} = 4{S^3}\\)\\(S = \\sqrt[3]{{\\frac{{{K_{sp}}}}{4}}} = \\sqrt[3]{{\\frac{{1 \\times {{10}^{ -11}}}}{4}}} = 1.35 \\times {10^{ - 4}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Let the solubility of an aqueous solution of \\(Mg{(OH)_2}\\) be x then its\\({k_{sp}}\\) is  ','(a)\\(4{x^3}\\)','(b)\\(108{x^5}\\)','(c)\\(27{x^4}\\)','(d)\\(9x\\)','1','Ionic Equlibrium-Final_final','2','Answer =  (a)\\(Mg{(OH)_2}\\)\\(\\rightleftharpoons{}\\)\\(\\mathop {M{g^{ +  +}}}\\limits_{(X)\\,\\,\\,}  + \\mathop {2O{H^ - }}\\limits_{{{(2X)}^2}} \\)\\({K_{sp}} = 4{X^3}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The solubility product of \\(BaS{O_4}\\) at \\(25^\\circ C\\) is \\(1.0 \\times {10^{- 9}}\\). What would be the concentration of \\({H_2}S{O_4}\\) necessary toprecipitate \\(BaS{O_4}\\) from a solution of \\(0.01\\,M\\,B{a^{2 + }}\\)ions','(a)\\({10^{ - 9}}\\)','(b)\\({10^{ - 8}}\\)','(c)\\({10^{ - 7}}\\)','(d)\\({10^{ - 6}}\\)','3','Ionic Equlibrium-Final_final','2','Answer =  (c)\\(BaS{O_4}\\)\\(\\rightleftharpoons{}\\)\\(\\mathop {\\mathop {B{a^{ +  +}}}\\limits_{(S)\\,\\,\\,\\,\\,\\,\\,} }\\limits_{0.01\\,\\,\\,\\,}  + \\mathop {\\mathop{SO_4^{ - \\, - }}\\limits_{(S)\\,\\,\\,\\,} }\\limits_{5\\,\\,\\,\\,} \\)\\({K_{sp}} = {S^2} = S \\times S = 0.01 \\times S\\)\\({S_{(SO_4^{2 - })}} = \\frac{{{K_{sp}}}}{{{S_{(B{a^{ +  + }})}}}} = \\frac{{1\\times {{10}^{ - 9}}}}{{0.01}} = {10^{ - 7}}\\)mole/litre',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The solubility in water of a sparingly soluble salt \\(A{B_2}\\) is \\(1.0\\times {10^{ - 5}}mol\\,{l^{ - 1}}\\). Its solubility product number will be','(a)\\(4 \\times {10^{ - 15}}\\)','(b)\\(4 \\times {10^{ - 10}}\\)','(c)\\(1 \\times {10^{ - 15}}\\)','(d)\\(1 \\times {10^{ - 10}}\\)','1','Ionic Equlibrium-Final_final','2','Answer =  (a)\\(A{B_2}\\) \\(\\rightleftharpoons{}\\) \\(\\mathop {{A^ + }\\,\\,\\,}\\limits_{1\\times {{10}^{ - 5}}} \\,\\, + \\,\\,\\,\\,\\,\\mathop {2{B^ - }}\\limits_{2 \\times{{10}^{ - 5}}} \\)\\({K_{sp}} = [1 \\times {10^{ - 5}}]\\,\\,{[2 \\times {10^{ - 5}}]^2} = 4 \\times {10^{- 15}}\\)',CURRENT_TIMESTAMP);
