INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which is the correct representation of the solubility product constant of\\(A{g_2}Cr{O_4}\\)[NCERT , 75]','(a)\\({[A{g^ + }]^2}\\,[CrO_4^{ - 2}]\\)','(b)\\([A{g^ + }]\\,\\,[CrO_4^{ - 2}]\\)','(c)\\([2A{g^ + }]\\,\\,[CrO_4^{ - 2}]\\)','(d)\\({[2A{g^ + }]^2}\\,\\,[CrO_4^{ - 2}]\\)','1','Ionic Equlibrium-Final_final','2','Answer =  (a)\\(A{g_2}Cr{O_4}\\)\\(\\rightleftharpoons{}\\)\\([2A{g^ + }] + [CrO_4^{ - \\, -}]\\)Hence \\({K_{sp}} = {[A{g^ + }]^2}\\,\\,[CrO_4^{ - \\, - }]\\)',CURRENT_TIMESTAMP);
