INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The solubility of \\(BaS{O_4}\\) in water is \\(2.33 \\times {10^{ -3}}\\,gm/litre.\\) Its solubility product will be (molecular weight of \\(BaS{O_4} =\\)','(a)\\(1 \\times {10^{ - 5}}\\)','(b)\\(1 \\times {10^{ - 10}}\\)','(c)\\(1 \\times {10^{ - 15}}\\)','(d)\\(1 \\times {10^{ - 20}}\\)','2','Ionic Equlibrium-Final_final','2','Answer =  (b)The solubility of \\(BaS{O_4}\\)in g/litre is given\\(2.33 \\times {10^{ - 3}}\\)in mole/litre.\\(n = \\frac{W}{{m.\\,wt}}\\)\\( = 1 \\times {10^{ - 5}}\\)\\( =\\,\\frac{{2.33 \\times {{10}^{ - 3}}}}{{Answer = }\\)Because \\(BaS{O_4}\\)is a compound\\({K_{sp}} = {S^2} = {[1 \\times {10^{ - 5}}]^2}\\)\\(= 1 \\times {10^{ - 10}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The solubility of \\(AgCl\\) in \\(0.2\\,\\,M\\,\\,NaCl\\) solution \\(({K_{sp}}\\)for\\(AgCl = 1.20 \\times {10^{ - 10}})\\) is','(a)\\(0.2\\,\\,M\\)','(b)\\(1.2 \\times {10^{ - 10}}\\,M\\)','(c)\\(0.2 \\times {10^{ - 10}}\\,M\\)','(d)\\(0.2 \\times {10^{ - 10}}\\,M\\)','4','Ionic Equlibrium-Final_final','2','Answer =  (d)\\(\\mathop {AgCl}\\limits_a \\)\\(\\rightleftharpoons{}\\)\\(\\mathop {A{g^ +}}\\limits_a  + \\mathop {C{l^ - }}\\limits_a \\)\\(\\mathop {NaCl}\\limits_{0.02} \\)\\(\\rightleftharpoons{}\\) \\(\\mathop {N{a^ +}}\\limits_{0.02\\,\\,\\,\\,\\,} \\,\\, + \\mathop {C{l^ - }}\\limits_{0.02\\,\\,\\,\\,\\,\\,} \\)\\({K_{sp}}\\,\\,AgCl = 1.20 \\times {10^{ - 10}}\\)\\({K_{sp}}\\,\\,AgCl = [A{g^ + }]\\,\\,[C{l^ - }]\\)\\( = a \\times [a + 0.2]\\)\\(= {a^2} + 0.2a\\)\\({a^2}\\)is a very small so it is a neglected.\\({K_{sp}}\\,\\,AgCl = 0.2a\\)\\(1.20 \\times {10^{ - 10}} = 0.2a\\)\\(a = \\frac{{1.20 \\times {{10}^{ - 10}}}}{{0.20}} = 6 \\times {10^{ - 10}}\\)mole',CURRENT_TIMESTAMP);
