INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','At \\({20^o}C,\\) the \\(A{g^ + }\\)ion concentration in a saturated solution of\\(A{g_2}Cr{O_4}\\) is\\(1.5 \\times {10^{ - 4}}\\,mole/litre.\\) At \\({20^o}C,\\)thesolubility product of \\(A{g_2}Cr{O_4}\\) would be','(a)\\(3.3750 \\times {10^{ - 12}}\\)','(b)\\(1.6875 \\times {10^{ - 10}}\\)','(c)\\(1.6875 \\times {10^{ - 12}}\\)','(d)\\(1.6875 \\times {10^{ - 11}}\\)','3','Ionic Equlibrium-Final_final','2','Answer =  (c)\\(AgCr{O_4}\\)\\(\\rightleftharpoons{}\\) \\(\\mathop {2A{g^ +}}\\limits_{{{(2S)}^2}}  + \\mathop {CrO_4^ - }\\limits_{S\\,\\,\\,} \\)\\({K_{sp}} = 4{S^3}\\) given \\(2S = 1.5 \\times {10^{ - 4}}\\)\\(\\therefore \\,{K_{sp}} = {(2S)^2} \\times S\\)\\( = {(1.5 \\times {10^{ - 4}})^2} \\times \\left( {\\frac{{1.5 \\times {{10}^{ -4}}}}{2}} \\right)\\)\\(= 1.Answer =  \\times {10^{ - 12}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The solubility of \\(PbC{l_2}\\)is','(a)\\(\\sqrt {{K_{sp}}} \\)','(b)\\(^3\\sqrt {{K_{sp}}} \\)','(c)\\(^3\\sqrt {\\frac{{{K_{sp}}}}{4}} \\) ','(d)\\(\\sqrt {8{K_{sp}}} \\)','3','Ionic Equlibrium-Final_final','2','Answer =  (c)\\(PbC{l_2}\\)  \\(\\rightleftharpoons{}\\)\\(\\mathop {P{b^{2 +}}}\\limits_{S\\,\\,}  + \\mathop {2C{l^ - }}\\limits_{S\\,\\,} \\)\\({K_{sp}}\\)of \\(PbC{l_2} = [P{b^{2 + }}] \\times {[C{l^ - }]^2}\\) ;\\({K_{sp}} =S \\times {(2S)^2}\\)\\({K_{sp}} = S \\times 4{S^2}\\)\\( = 4{S^3}\\); \\({S^3} = \\frac{{{K_{sp}}}}{4}\\);\\(S = \\sqrt[3]{{\\frac{{{K_{sp}}}}{4}}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The solubility product of \\(1 \\times {10^{ - 5}}\\) is \\(1.44 \\times {10^{ -4}}\\) at\\({o}C\\). The solubility of silver chloride in boiling water may be','(a)\\(0.72 \\times {10^{ - 4}}\\,M\\)','(b)\\(1.20 \\times {10^{ - 2}}\\,M\\)','(c)\\({80^o}C,\\)','(d)\\(0.5\\,M\\,HCOOH,\\)','','Ionic Equlibrium-Final_final','2','Answer = (b)AgCl  \\(\\rightleftharpoons{}\\) \\([A{g^ + }]\\,\\,[C{l^ - }]\\) ;\\({K_{sp}} =S \\times S\\) ; \\({K_{sp}} = {S^2}\\)\\(S = \\sqrt {{K_{sp}}} \\)\\( = \\sqrt {1.44 \\times {{10}^{ - 4}}} \\) \\( = 1.20\\times {10^{ - 2}}\\)M.',CURRENT_TIMESTAMP);
