INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','If the concentration of lead iodide in its saturated solution at\\({25^o}C\\)be \\(2 \\times {10^{ - 3}}\\)moles per litre, then its solubilityproduct is','(a)\\(4 \\times {10^{ - 6}}\\)','(b)\\(8 \\times {10^{ - 12}}\\)','(c)\\(6 \\times {10^{ - 9}}\\)','(d)\\(32 \\times {10^{ - 9}}\\)','4','Ionic Equlibrium-Final_final','2','Answer =  (d)\\(\\mathop {Pb{I_2}}\\limits_{{K_{sp}}} \\) \\(\\rightleftharpoons{}\\)\\(\\mathop {P{b^{ +  + }}}\\limits_{S\\,\\,\\,\\,\\,\\,}  + \\mathop {2{I^ -}}\\limits_{2S\\,\\,\\,} \\)\\({K_{sp}} = 4{S^3} = 4 \\times {[2 \\times {10^{ - 3}}]^3}\\)\\(= 32 \\times {10^{ - 9}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The precipitate of  \\(Ca{F_2}\\)\\(({K_{sp}} = 1.7 \\times {10^{ - 10}})\\) isobtained when equal volumes of the following are mixed','(a)\\({10^{ - 4}}M\\,C{a^{2 + }} + {10^{ - 4}}M\\,{F^ - }\\)','(b)\\({10^{ - 2}}M\\,C{a^{2 + }} + {10^{ - 3}}M\\,{F^ - }\\)','(c)Both','(d)None of these','2','Ionic Equlibrium-Final_final','2','Answer =  (b)When ionic product is greater than \\({K_{sp}}\\) then precipitation occur\\({K_{sp}} < {10^{ - 2}}M\\,\\,C{a^{2 + }} + {10^{ - 3}}M\\,\\,{F^ - }\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In the reaction: \\({H_2}S\\) \\(\\rightleftarrows{}\\) \\(2{H^ + } + {S^{ -  -}}\\), when \\(N{H_4}OH\\) is added, then ','(a)\\({S^{ -  - }}\\) is precipitate','(b)No action takes places','(c)Concentration of \\({S^{ -  - }}\\) decreases','(d)Concentration of \\({S^{ -  - }}\\) increases','4','Ionic Equlibrium-Final_final','2','Answer =  (d)In IV\\(^{th}\\) group the \\({S^{2 - }}\\)concentration increase when addedthe \\(N{H_4}OH\\,\\,\\,\\,{\\rm{because}}\\)\\(N{H_4}OH\\)\\(\\rightleftharpoons{}\\)  \\(NH_4^ +  + O{H^ - }\\)\\({H_2}S\\) \\(\\rightleftharpoons{}\\) \\(2{H^ + } + {S^{2 - }}\\)\\(O{H^ - } + {H^ + }\\) \\(\\rightleftharpoons{}\\) \\({H_2}O\\). So that \\({S^{2 -}}\\)is increased.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','What is the minimum concentration of \\(SO_4^{2 - }\\) required to precipitate\\(BaS{O_4}\\) in a solution containing \\(1.0 \\times {10^{ - 4}}mol\\,\\,\\,B{a^{2 +}}\\)? (\\({K_{sp}}\\)for \\(BaS{O_4}\\) is \\(4 \\times {10^{ - 10}}\\))','(a)\\(4 \\times {10^{ - 10}}M\\)','(b)\\(2 \\times {10^{ - 7}}M\\)','(c)\\(4 \\times {10^{ - 6}}M\\)','(d)\\(2 \\times {10^{ - 3}}M\\)','','Ionic Equlibrium-Final_final','2','Answer = (c)\\(BaS{O_4}\\)\\(\\rightleftharpoons{}\\)\\(B{a^{2 + }} + SO_4^{2 - }\\)\\({K_{sp}} = {S^2} \\Rightarrow S = \\sqrt {{K_{sp}}} \\)\\({K_{sp}} = [B{a^{2 + }}] \\times [SO_4^{2 - }];\\)\\(4 \\times {10^{ - 10}} = [1 \\times {10^{ - 4}}] \\times [SO_4^{2 - }]\\)\\([SO_4^{2 - }] = \\frac{{4 \\times {{10}^{ - 10}}}}{{1 \\times {{10}^{ - 4}}}} = 4\\times {10^{ - 6}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Solubility product for salt \\(A{B_2}\\) is \\(4 \\times {10^{ - 12}}\\). Calculatesolubility  ','(a)\\(1 \\times {10^{ - 3}}gm\\,\\,mol\\,/\\,litre\\)','(b)\\(1 \\times {10^{ - 5}}gm\\,\\,mol\\,/\\,litre\\)','(c)\\(1 \\times {10^{ - 4}}gm\\,\\,mol\\,/\\,litre\\)','(d)\\(1 \\times {10^{ - 2}}gm\\,\\,mol\\,/\\,litre\\)','3','Ionic Equlibrium-Final_final','2','Answer =  (c)\\(A{B_2}\\)\\(\\rightleftharpoons{}\\)\\(\\mathop {{A^{2 + }}}\\limits_{(S)\\,} + \\mathop {2{B^ - }}\\limits_{{{(2S)}^2}} \\)\\({K_{sp}} = 4{S^3}\\)\\(S = \\sqrt[3]{{\\frac{{{K_{sp}}}}{4}}} = \\sqrt[3]{{\\frac{{4 \\times {{10}^{ -12}}}}{4}}}\\)\\(= 1 \\times {10^{ - 4}}\\,\\,gm.\\,mol/litre\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Solubility product of a salt AB is \\(1 \\times {10^{ - 8}}\\) in a solution inwhich concentration of A is \\({10^{ - 3}}M\\). The salt will precipitate when theconcentration of B becomes more than','(a)\\({10^{ - 4}}M\\)','(b)\\({10^{ - 7}}M\\)','(c)\\({10^{ - 6}}M\\)','(d)\\({10^{ - 5}}M\\)','','Ionic Equlibrium-Final_final','2','Answer = (d)\\([B] = \\frac{{{K_{sp}}AB}}{{[A]}} = \\frac{{1 \\times {{10}^{ -8}}}}{{{{10}^{ - 3}}}} = 1 \\times {10^{ - 5}}M\\)Where ionic product \\(>\\) \\({K_{sp}},\\,ppt\\) formed\\(\\therefore \\) 8 should be more then \\({10^{ - 5}}M\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','At equilibrium, if to a saturated solution of \\(NaCl,\\,HCl\\) is passed,\\(NaCl\\) gets precipitated because ','(a)\\(HCl\\) is a strong acid','(b)Solubility of \\(NaCl\\) decreases','(c)Ionic product of \\(NaCl\\) becomes greater than its \\({K_{sp}}\\)','(d)\\(HCl\\) is a weak acid','3','Ionic Equlibrium-Final_final','2','Answer =  (c)\\(NaC{l_{({\\rm{s}})}}\\) \\(\\rightleftharpoons{}\\) \\(Na_{({\\rm{aq}})}^ + + Cl_{({\\rm{aq}})}^ - \\)\\(HCl\\)\\(\\rightleftharpoons{}\\) \\({H^ + } + C{l^ - }\\). The increase in \\([C{l^- }]\\)brings in an increase in \\([N{a^ + }]\\)\\([C{l^ - }]\\)which will lead forbackward reaction because\\({K_{sp}}(NaCl) = [N{a^ + }]\\,\\,[C{l^ - }]\\)means Ionic product \\( \\ge {K_{sp}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The solubility product of \\(BaS{O_4}\\) is \\(1.3 \\times {10^{ - 9}}\\). Thesolubility of this salt in pure water will be','(a)\\(1.69 \\times {10^{ - 9}}mol\\,litr{e^{ - 1}}\\)','(b)\\(1.69 \\times {10^{ -18}}mol\\,litr{e^{ - 1}}\\)','(c)\\(3.6 \\times {10^{ - 18}}mol\\,litr{e^{ - 1}}\\)','(d)\\(3.6 \\times {10^{ -5}}mol\\,litr{e^{ - 1}}\\)','4','Ionic Equlibrium-Final_final','2','Answer =  (d)\\(BaS{O_4}\\)\\(\\rightleftharpoons{}\\)\\(\\mathop {B{a^{ +  +}}}\\limits_{(S)\\,\\,\\,\\,}  + \\mathop {SO_4^{ - \\, - }}\\limits_{(S)\\,\\,\\,\\,} \\)\\({K_{sp}} = {S^2};\\,\\,S = \\sqrt {{K_{sp}}}  = \\sqrt {1.3 \\times {{10}^{ - 9}}}\\)\\(= 3.6 \\times {10^{ - 5}}\\,\\,\\,mol/litre\\)',CURRENT_TIMESTAMP);
