INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A saturated solution of \\(A{g_2}S{O_4}\\)is \\(2.5 \\times {10^{ - 2}}\\)M; Thevalue of its solubility product is','(a)\\(62.5 \\times {10^{ - 6}}\\)','(b)\\(6.25 \\times {10^{ - 4}}\\)','(c)\\(15.625 \\times {10^{ - 6}}\\)','(d)\\(3.125 \\times {10^{ - 6}}\\)','','Ionic Equlibrium-Final_final','2','Answer = (a)For \\(A{g_2}S{O_4} \\to \\mathop {2A{g^ + }}\\limits_{2x}  + \\mathop {SO_4^{-  - }}\\limits_x \\)\\({K_{sp}} = {(2x)^2}.x\\) ; \\({K_{sp}} = 4{x^3}\\) ; \\({K_{sp}} = 4 \\times {(2.5\\times {10^{ - 2}})^3}\\)\\({K_{sp}} = 62.5 \\times {10^{ - 6}}\\)',CURRENT_TIMESTAMP);
