INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Given pH of a solution A is 3and it is mixed with another solution B having pH 2. If both mixed thenresultant pH of the solution will be','(a)3.2','(b)1.9','(c)3.4','(d)3.5','2','Ionic Equlibrium-Final_final','2','Answer =  (b)pH of the solution A = 3[H\\(^{+}\\)]\\(_{A}\\) = 10\\(^{-3}\\) M.pH of the solution B = 2[H\\(^{+}\\)]\\(_{B}\\) =10\\(^{-2}\\) M[H\\(^{+}\\)] = 10\\(^{-3}\\) + 10\\(^{-2}\\) = 10\\(^{-3}\\) + 10 \\(\\times{}\\)10\\(^{-3}\\) = 11 \\(\\times{}\\) 10\\(^{-3}\\).pH = - log(11 \\(\\times{}\\) 10\\(^{-3}\\)) = 3 - log 11= 3 - 1.04= 1.95',CURRENT_TIMESTAMP);
