INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','For a weak acid \\(HA\\) with dissociation constant  \\({10^{ - 9}},\\,\\,pOH\\) ofits 0.1 M solution is ','(a)9','(b)3','(c)11','(d)101','4','Ionic Equlibrium-Final_final','2','Answer =  (d)Dissociation constant of \\(HA = {10^{ - 9}}\\)\\(HA\\)\\(\\rightleftharpoons{}\\)\\({H^ + } + {A^ - }\\)\\([{H^ + }] = \\sqrt {\\frac{{{K_a}}}{C}} \\)\\( = \\sqrt {\\frac{{{{10}^{ -9}}}}{{0.1}}} \\)\\(\\therefore \\,pH = 4\\)\\([{H^ + }] = {10^{ - 4}};\\)\\(\\,pH + pOH = 14\\)\\(pOH = 14 - pH\\)\\( = 14 - 4\\)\\(pOH = 10;\\)1',CURRENT_TIMESTAMP);
