INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The standard electrode potential for the two electrode \\({A^ + }/A\\) and\\({B^ + }/B\\) are respectively 0.5 \\(V\\) and 0.75 \\(V\\). The \\(emf\\) of the givencell  \\(A\\,\\,|\\,\\,{A^ + }(a = 1)\\,\\,||\\,\\,{B^ + }(a = 1)\\,\\,|\\,\\,B\\) will be','(a)1.25 \\(V\\)','(b)- 1.25 \\(V\\)','(c)- 0.25 \\(V\\)','(d)0.25 \\(V\\)','4','Electrochemistry','2','Answer =  (d)\\(\\mathop {A|{A^ + }(a = 1)||}\\limits^{{\\rm{Anode}}} \\mathop {{B^ + }(a= 1){\\rm{|}}B}\\limits^{{\\rm{Cathode}}} \\)\\(EMF = {E_{cathode}} - {E_{anode}}\\)\\( = 0.75 - (0.5)\\); \\(EMF = 0.25\\,V\\).',CURRENT_TIMESTAMP);
