INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The standard reduction potential for \\(F{e^{2 + }}/Fe\\) and \\(S{n^{2 +}}/Sn\\) electrodes are \\( - 0.44\\) and \\( - 0.14\\) volt respectively. For thegiven cell reaction \\(F{e^{2 + }} + Sn\\, \\to Fe + S{n^{2 + }}\\), the standard EMFis','(a)+ 0.30 V','(b)- 0.58 V','(c)+ 0.58 V','(d)- 0.30 V','1','Electrochemistry','2','Answer =  (a)\\(E_{{\\rm{cell}}}^o = E_{{\\rm{cathode}}}^o - E_{{\\rm{anode}}}^o\\)\\( =0.34 - ( - \\,0.76)\\)\\( = 1.10\\,\\,V\\).',CURRENT_TIMESTAMP);
