INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In any chemical reaction, equilibrium is supposed to be establish when','(a)Mutual opposite reactions undergo','(b)Concentration of reactants and resulting products are equal','(c)Velocity of mutual reactions become equal','(d)The temperature of mutual opposite reactions become equal','','Chemical Equilibrium-Final','2','Answer = (c)When rate of forward reaction is equal to the rate of backward reactionthen equilibrium is supposed to be established.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following conditions represents an equilibrium','(a)Freezing of ice in a open vessel, temperature of ice is constant(b)Few drops of water is present along with air in  a balloon, temperature ofballoon is constant(c)Water is boiling in an open vessel over stove, temperature of water isconstant(d)All the statements ','','(b)Few drops of water is present along with air in  a balloon, temperature ofballoon is constant(c)Water is boiling in an open vessel over stove, temperature of water isconstant(d)All the statements (a), ','(b) and (c) are correct for the equilibrium','','Chemical Equilibrium-Final','2','Answer = (b)Equilibrium can be achieved only in closed vessel.',CURRENT_TIMESTAMP);
