INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In the reversible reaction \\(A + B\\)\\(\\rightleftharpoons{}\\) \\(C + D\\), theconcentration of each C and D at equilibrium was 0.8 mole/litre, then theequilibrium constant \\({K_c}\\) will be ','(a)\\(\\frac{1}{4}\\) ','(b)\\(\\frac{1}{2}\\)','(c)1 ','(d)4','4','Chemical Equilibrium-Final','2','Answer =  (d)Suppose 1 mole of A and B each taken then 0.8 mole/litre of C and D eachformed remaining concentration of A and B will be (1 - 0.8) = 0.2mole/litre each.\\(Kc = \\frac{{[C]\\,\\,[D]}}{{[A]\\,\\,[B]}} = \\frac{{0.8 \\times 0.8}}{{0.2 \\times0.2}} = 16.0\\)',CURRENT_TIMESTAMP);
