INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The decomposition of \\({N_2}{O_4}\\) to \\(N{O_2}\\) is carried out at \\(\\)in chloroform. When equilibrium has been established, 0.2 mol of \\({N_2}{O_4}\\)and \\(2 \\times {10^{ - 3}}\\) mol of \\(N{O_2}\\)  are present in 2 litre solution.The equilibrium constant for reaction \\({N_2}{O_4}\\) \\(\\rightleftharpoons{}\\)\\(2N{O_2}\\) is','(a)\\(1 \\times {10^{ - 2}}\\) ','(b)\\(2 \\times {10^{ - 3}}\\)','(c)\\(1 \\times {10^{ - 5}}\\) ','(d)\\(2 \\times {10^{ - 5}}\\)','3','Chemical Equilibrium-Final','2','Answer =  (c)\\(K = \\frac{{{{[N{O_2}]}^2}}}{{[{N_2}{O_4}]}} = \\frac{{{{\\left[ {2\\times {{\\frac{{10}}{2}}^{ - 3}}} \\right]}^2}}}{{\\left[ {\\frac{{.2}}{2}}\\right]}} = \\frac{{{{10}^{ - 6}}}}{{{{10}^{ - 1}}}} = {10^{ - 5}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In a reaction \\(A + B\\)\\(\\rightleftharpoons{}\\) \\(C + D\\), the concentrationsof A, B, C and D (in moles/litre) are 0.5, 0.8, 0.4 and 1.0 respectively. Theequilibrium constant is','(a)0.1','(b)1.0','(c)10','(d)\\(\\infty \\)','2','Chemical Equilibrium-Final','2','Answer =  (b)For A + B \\(\\rightleftharpoons{}\\) C + D\\(K = \\frac{{[C]\\,\\,[D]}}{{[A]\\,\\,[B]}} = \\frac{{0.4 \\times 1}}{{0.5 \\times 0.8}}= 1.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In a chemical equilibrium  \\(A + B\\) \\(\\rightleftharpoons{}\\) \\(C + D\\),when one mole each of the two reactants are mixed, 0.6 mole each of the productsare formed. The equilibrium constant calculated is','(a)1','(b)0.36','(c)2.25','(d)4/9','3','Chemical Equilibrium-Final','2','Answer =  (c) A+ B \\(\\rightleftharpoons{}\\)C+ DInitial                                1   1       0   0remaining at equilibrium 0.4 0.4  0.6  0.6\\(K = \\frac{{[C]\\,\\,[D]}}{{[A]\\,\\,[B]}} = \\frac{{0.6 \\times 0.6}}{{0.4 \\times0.4}} = \\frac{{36}}{{16}} = 2.25.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','For the reaction \\({N_{2(g)}} + 3{H_{2(g)}}\\) \\(\\rightleftharpoons{}\\)\\(2N{H_{3(g)}}\\), the correct expression of equilibrium constant  K is','(a)\\(K = \\frac{{{{[N{H_3}]}^2}}}{{[{N_2}]{{[{H_2}]}^3}}}\\) ','(b)\\(K =\\frac{{[{N_2}]{{[{H_2}]}^3}}}{{{{[N{H_3}]}^2}}}\\)','(c)\\(K = \\frac{{2[N{H_3}]}}{{[{N_2}] \\times 3[{H_2}]}}\\) ','(d)\\(K = \\frac{{[{N_2}]\\times 3[{H_2}]}}{{2[N{H_3}]}}\\)','','Chemical Equilibrium-Final','2','Answer = (a)\\(K = \\frac{{{{[N{H_3}]}^2}}}{{[{N_2}]\\,\\,{{[{H_2}]}^3}}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A + B \\(\\rightleftharpoons{}\\) C + D. If finally the concentration of A andB are both equal but at equilibrium concentration of D will be twice of that of Athen what will be the equilibrium constant of reaction.','(a)4 / 9','(b)9/ 4','(c)1 / 9','(d)4','4','Chemical Equilibrium-Final','2','Answer =  (d)A + B \\(\\rightleftharpoons{}\\) C + Dx     x       0     02x   2x\\({K_c} = \\frac{{[C][D]}}{{[A][B]}} = \\frac{{2x\\,.\\,2x}}{{x\\,.\\,x}} = 4\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','If in the reaction \\({N_2}{O_4} = 2N{O_2},\\,\\alpha \\) is that part of\\({N_2}{O_4}\\) which dissociates, then the number of moles at equilibrium willbe','(a)3','(b)1','(c)\\({(1 - \\alpha )^2}\\) ','(d)\\((1 + \\alpha )\\)','4','Chemical Equilibrium-Final','2','Answer =  (d)\\(\\mathop {\\mathop {{N_2}{O_4}}\\limits_1 }\\limits_{(1 - \\alpha )}\\)\\(\\rightleftharpoons{}\\) \\(\\mathop {\\mathop {2N{O_2}}\\limits_0}\\limits_{2\\,\\alpha } \\)total mole at equilibrium \\( = (1 - \\alpha ) + 2\\alpha \\)\\( = 1 + \\alpha \\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In the gas phase reaction, \\({C_2}{H_4} + {H_2}\\)\\(\\rightleftharpoons{}\\)\\({C_2}{H_6}\\), the equilibrium constant can be expressed in units of','(a)\\(litr{e^{ - 1}}\\,mol{e^{ - 1}}\\)','(b)\\(litre\\,mol{e^{ - 1}}\\)','(c)\\(mol{e^2}\\,litr{e^{ - 2}}\\) ','(d)\\(mole\\,litr{e^{ - 1}}\\)','2','Chemical Equilibrium-Final','2','Answer =  (b)\\(K = \\frac{{[{C_2}{H_6}]}}{{[{C_2}{H_4}]\\,\\,[{H_2}]}} =\\frac{{[mole/litre]}}{{[mole/litre]\\,\\,[mole/litre]}}\\)= litre/mole. or litre mole\\(^{-1}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A quantity of \\(PC{l_5}\\) was heated in a 10 litre vessel at \\({o}C\\);\\(PC{l_5}(g)\\)\\(\\rightleftharpoons{}\\) \\(PC{l_3}(g) + C{l_2}(g)\\). At equilibriumthe vessel contains 0.1 mole of \\(PC{l_5}\\,0.20\\)  mole of \\(PC{l_3}\\) and 0.2mole of \\(C{l_2}\\). The equilibrium constant of the reaction is','(a)0.02','(b)0.05','(c)0.04','(d)0.025','3','Chemical Equilibrium-Final','2','Answer =  (c)\\({K_c} = \\frac{{[PC{l_3}]\\,\\,[C{l_2}]}}{{[PC{l_5}]}} =\\frac{{\\frac{{0.2}}{{10}} \\times \\frac{{0.2}}{{10}}}}{{\\left[ {{{0.1}\\mathord{\\left/{\\vphantom {{0.1} {10}}} \\right.} {10}}} \\right]}} = 0.04\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A mixture of 0.3 mole of \\({H_2}\\) and 0.3 mole of \\({I_2}\\) is allowed toreact in a 10 litre  evacuated flask at \\({o}C\\). The reaction is \\({H_2} +{I_2}\\)\\(\\rightleftharpoons{}\\) \\(2HI\\), the \\(K\\) is found to be 64. The amountof unreacted \\({I_2}\\) at equilibrium is','(a)0.15 mole','(b)0.06 mole','(c)0.03 mole','(d)0.2 mole','2','Chemical Equilibrium-Final','2','Answer =  (b)\\({K_c} = \\frac{{{{[HI]}^2}}}{{[{H_2}]\\,\\,[{I_2}]}}\\);  \\(64 =\\frac{{{x^2}}}{{0.03 \\times 0.03}}\\)\\({x^2} = 64 \\times 9 \\times {10^{ - 4}}\\)\\(x = 8 \\times 3 \\times {10^{ - 2}} = 0.24\\)x is the amount of HI at equilibrium amount of \\({I_2}\\)at equilibrium will be\\(0.30 - 0.24 = 0.06\\)',CURRENT_TIMESTAMP);
