INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','An amount of solid \\(N{H_4}HS\\) is placed in a flask already containingammonia gas at a certain temperature and 0.50 atm. pressure. Ammonium hydrogensulphide decomposes to yield \\(N{H_3}\\) and \\({H_2}S\\) gases in the flask. Whenthe decomposition reaction reaches equilibrium, the total pressure in the flaskrises to 0.84 atm. The equilibrium constant for \\(N{H_4}HS\\) decomposition atthis temperature is','(a)0.30','(b)0.18','(c)0.17','(d)0.11','4','Chemical Equilibrium-Final','2','Answer =  (d)\\(\\mathop {\\mathop {N{H_4}HS}\\limits_{\\rm{a}} }\\limits_{{\\rm{a}} -{\\rm{x}}} \\) \\(\\rightleftharpoons{}\\) \\(\\mathop {\\mathop {N{H_{3\\,(g)}} +{H_2}{S_{(g)}}}\\limits_{0.5{\\rm{atm}}\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,}}\\limits_{0.5 + {\\rm{x}}\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,{\\rm{x}}\\,\\,\\,\\,\\,\\,} \\)Total pressure \\( = 0.5 + 2x = 0.84\\)i.e., \\(x = 0.17\\)\\({K_p} = {P_{N{H_3}}}.{P_{{H_2}S}}=(0.67).(0.17)=0.Answer = \\)',CURRENT_TIMESTAMP);
