INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','If the equilibrium constant of the reaction \\(2HI\\) \\(\\rightleftharpoons{}\\)\\({H_2} + {I_2}\\) is 0.25, then the equilibrium constant of the reaction \\({H_2}+ {I_2}\\)\\(\\rightleftharpoons{}\\) \\(2HI\\) would be[MP PMT , 95]','(a)1.0','(b)2.0','(c)3.0','(d)4.0','4','Chemical Equilibrium-Final','2','Answer =  (d)\\({K_1}\\)for reaction 2HI \\(\\rightleftharpoons{}\\)\\({H_2} + {I_2}\\)is 0.25 \\({K_2}\\) for reaction \\({H_2} + {I_2}\\)\\(\\rightleftharpoons{}\\) 2HI will be \\({K_2} = \\frac{1}{{{K_1}}} = \\frac{1}{{0.25}} = 4\\)Because II\\(^{nd}\\) reaction is reverse of I\\(^{st}\\).',CURRENT_TIMESTAMP);
