INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The equilibrium constant for the reversible reaction, \\({N_2} + 3{H_2}\\)\\(\\rightleftharpoons{}\\) \\(2N{H_3}\\) is \\(K\\) and for the reaction\\(\\frac{1}{2}{N_2} + \\frac{3}{2}{H_2}\\) \\(\\rightleftharpoons{}\\) \\(N{H_3}\\) theequilibrium constant is \\(K\\'\\). \\(K\\) and \\(K\\'\\) will be related as','(a)\\(K = K\\'\\) ','(b)\\(K\\' = \\sqrt K \\)','(c)\\(K = \\sqrt {K\\'} \\)','(d)\\(K \\times K\\' = 1\\)','2','Chemical Equilibrium-Final','2','Answer =  (b)\\(K\\' = {K^n};\\) Hence \\(n = \\frac{1}{2}\\)\\(\\therefore \\,\\,K\\' = {K^{1/2}} = \\sqrt K\\)',CURRENT_TIMESTAMP);
