INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The equilibrium constant for the reaction \\({N_2} +3{H_2}\\)\\(\\rightleftharpoons{}\\)\\(2N{H_3}\\) is \\(K,\\) then the equilibriumconstant for the equilibrium \\(N{H_3}\\)\\(\\rightleftharpoons{}\\)\\(\\frac{1}{2}{N_2}+ \\frac{3}{2}{H_2}\\) is','(a)\\(1/K\\) ','(b)\\(1/{K^2}\\)','(c)\\(\\sqrt K \\) ','(d)\\(\\frac{1}{{\\sqrt K }}\\)','4','Chemical Equilibrium-Final','2','Answer =  (d)\\({K_1}\\) for \\({N_2} + 3{H_2}\\) \\(\\rightleftharpoons{}\\) \\(2N{H_3}\\)\\({K_2}\\) for \\(N{H_3}\\) \\(\\rightleftharpoons{}\\) \\(\\frac{1}{2}{N_2} +\\frac{3}{2}{H_2}\\)\\({K_1} \\times {K_2} = \\frac{{{{[N{H_3}]}^2}}}{{[{N_2}]\\,\\,{{[{H_2}]}^3}}} \\times\\frac{{{{[{N_2}]}^{{1 \\mathord{\\left/{\\vphantom {1 2}} \\right.} 2}}}{{[{H_2}]}^{{3 \\mathord{\\left/{\\vphantom {3 2}} \\right.} 2}}}}}{{[N{H_3}]}}\\)\\({K_1} \\times {K_2} = \\frac{1}{{{K_2}}}\\)\\({K_2} = \\frac{1}{{\\sqrt {{K_1}} }};\\)',CURRENT_TIMESTAMP);
