INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','For the reaction\\(2N{O_{2(g)}}\\)\\(\\rightleftharpoons{}\\)\\(2N{O_{(g)}} +{O_{2(g)}}\\)\\(_{,}\\)\\({K_c} = 1.8 \\times {10^{ - 6}}\\) at \(^\\circ{}\\)C. At\(^\\circ{}\\)C the K\\(_{c }\\)for \\(N{O_{(g)}}\\)+\\(\\frac{1}{2}{O_{2(g)}}\\)\\(\\rightleftharpoons{}\\) }\\(N{O_{2(g)}}\\) is','(a)\\(1.95 \\times {10^{ - 3}}\\)','(b)\\(1.95 \\times {10^3}\\)','(c)\\(7.5 \\times {10^2}\\)','(d)\\(0.9 \\times {10^6}\\)','3','Chemical Equilibrium-Final','2','Answer =  (c)\\({K_{{c_1}}} = \\frac{{{{[NO]}^2}[{O_2}]}}{{{{[N{O_2}]}^2}}} = 1.8\\times {10^{ - 6}}\\)\\(\\Rightarrow{}\\) \\({K_{{c_2}}} =\\frac{{[N{O_2}]}}{{[NO]\\,\\,{{[{O_2}]}^{{1 \\mathord{\\left/{\\vphantom {1 2}} \\right.} 2}}}}}\\)\\({K_{{c_1}}} = \\frac{1}{{K_{{c_2}}^2}}\\); \\(1.8 \\times {10^{ - 6}} =\\frac{1}{{{K_{{c_2}}}^2}}\\)\\(\\Rightarrow{}\\)\\({K_{{c_2}}} = 7.5 \\times {10^2}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','If for \\({H_2}_{(g)} +\\frac{1}{2}{S_{2(S)}}\\)\\(\\rightleftharpoons{}\\)\\({H_2}{S_{(g)}}\\) and\\({H_{2(g)}} + B{r_{2(g)}}\\)\\(\\rightleftharpoons{}\\)\\(2HB{r_{(g)}}\\)The equilibrium constants are K\\(_{1 }\\)and K\\(_{2 }\\)respectively, the reaction\\(B{r_2}_{(g)} + {H_2}{S_{(g)}}\\)\\(\\rightleftharpoons{}\\)\\(2HB{r_{(g)}} +\\frac{1}{2}{S_{2(S)}}\\) would have equilibrium constant ','(a)\\({K_1}\\; \\times \\;{K_2}\\)','(b)\\(_{ }\\)\\({K_1}/{K_2}\\)','(c)\\({K_2}/{K_1}\\)','(d) \\(K_2^2/{K_1}\\)','3','Chemical Equilibrium-Final','2','Answer =  (c)\\({K_1} = \\frac{{[{H_2}S]}}{{[{H_2}]\\,\\,{{[{S_2}]}^{{1 \\mathord{\\left/{\\vphantom {1 2}} \\right.} 2}}}}}\\); \\({K_2} =\\frac{{{{[HBr]}^2}}}{{[{H_2}]\\,\\,[B{r_2}]}}\\)\\({K_3} = \\frac{{{{[HBr]}^2} \\times {{[{S_2}]}^{{1 \\mathord{\\left/{\\vphantom {1 2}} \\right.} 2}}}}}{{[B{r_2}]\\, \\times [{H_2}S]}}\\)\\(\\frac{{{K_2}}}{{{K_1}}} = {K_3};\\)',CURRENT_TIMESTAMP);
