INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','If equilibrium constant for reaction\\(2AB\\)\\(\\rightleftharpoons{}\\)\\({A_2} + {B_2}\\), is 49, then the equilibrium constant for reaction AB \\(\\rightleftharpoons{}\\)}\\(\\frac{1}{2}{A_2} + \\frac{1}{2}{B_2}\\), will be','(a)7','(b)20','(c)49','(d)21','1','Chemical Equilibrium-Final','2','Answer =  (a)\\(2AB\\)\\(\\rightleftharpoons{}\\) \\({A_2} + {B_2}\\)\\({K_c} = \\frac{{[{A_2}]\\,\\,[{B_2}]}}{{{{[AB]}^2}}}\\)For reaction \\(AB\\) \\(\\rightleftharpoons{}\\) \\(\\frac{1}{2}{A_2} +\\frac{1}{2}{B_2}\\)\\({K_c}\\' = \\frac{{{{[{A_2}]}^{{1 \\mathord{\\left/{\\vphantom {1 2}} \\right.} 2}}}{{[{B_2}]}^{{1 \\mathord{\\left/{\\vphantom {1 2}} \\right.} 2}}}}}{{[AB]}}\\);  \\({K_c}\\' = \\sqrt {{K_c}}  =\\sqrt {49}  = 7\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In the manufacture of ammonia by Haber\\'s process,\\({N_{2(g)}} + 3{H_2}\\) \\(\\rightleftharpoons{}\\)\\(2N{H_{3(g)}} + 92.3kJ\\),which of the following conditions is unfavourable','(a)Increasing the temperature','(b)Increasing the pressure','(c)Reducing the temperature','(d)Removing ammonia as it is formed','1','Chemical Equilibrium-Final','2','Answer =  (a)For this reaction \\(\\Delta n\\) is negative & \\(\\Delta H\\) is positiveso it take forward by decrease in temperature.',CURRENT_TIMESTAMP);
