INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Consider the following reversible reaction at equilibrium,\\(2{H_2}{O_{(g)}}\\) \\(\\rightleftharpoons{}\\) \\(2{H_{2(g)}} + {O_{2(g)}}\\);\\(\\Delta H = 7\\,kJ\\)Which one of the following changes in conditions will lead to maximumdecomposition of \\({H_2}{O_{(g)}}\\)','(a)Increasing both temperature and pressure','(b)Decreasing temperature and increasing pressure','(c)Increasing temperature and decreasing pressure','(d)Increasing temperature at constant pressure','3','Chemical Equilibrium-Final','2','Answer =  (c)\\(\\Delta H\\) is positive so reaction move forward by increase intemperature & value of \\(\\Delta n = 3 - 2 =  + 1\\) is positive so it forwardwith decrease in pressure.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','For reaction, \\(2A(g)\\)\\(\\rightleftharpoons{}\\) \\(3C(g) + D(s)\\), the valueof \\({K_c}\\)will be equal to','(a)\\({K_p}(RT)\\)','(b)\\({K_p}/RT\\)','(c)\\( = {K_p}\\)','(d)None of these','','Chemical Equilibrium-Final','2','Answer = (b)\\(2A(g)\\)         \\(3C(g) + D(s)\\)For this reaction, \\(\\Delta {n_g} = 3 - 2 = 1\\)\\(\\therefore \\,\\,{K_p} = {K_c}{[RT]^1}\\) or \\(\\frac{{{K_p}}}{{{K_c}}} = RT\\) or\\({K_c} = \\frac{{{K_p}}}{{RT}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In the reaction, \\({A_2}(g) + 4{B_2}(g)\\)\\(\\rightleftharpoons{}\\)\\(2A{B_4}(g)\\)\\(\\Delta H < 0\\)the formation of \\(A{B_4}\\)is will be favoured at','(a)Low temperature, high pressure','(b)High temperature, low pressure','(c)Low temperature, low pressure','(d)High temperature, high pressure','1','Chemical Equilibrium-Final','2','Answer =  (a)According to Le-Chatelier principle exothermic reaction is forwarded bylow temperature, in forward direction number of moles is less, hence pressure ishigh.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The formation of \\(S{O_3}\\) takes place according to the following reaction,\\(2S{O_2} + {O_2}\\)\\(\\rightleftharpoons{}\\) \\(2S{O_3}\\); \\(\\Delta H =  -45.2\\;kcal\\)The formation of \\(S{O_3}\\) is favoured by','(a)Increasing in temperature','(b)Removal of oxygen','(c)Increase of volume','(d)Increasing of pressure','4','Chemical Equilibrium-Final','2','Answer =  (d)In this reaction \\(\\Delta H\\) is negative so reaction move forward bydecrease in temperature while value of \\(\\Delta n = 2 - 3 =  - 1\\)i.e., negativeso the reaction move forward by increase in pressure.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','What is the effect of increasing pressure on the dissociation of \\(PC{l_5}\\)according to the equation\\(PC{l_{5(g)}}\\)\\(\\rightleftharpoons{}\\) \\(PC{l_{3(g)}} + C{l_{2(g)}} - x\\;cal\\)','(a)Dissociation decreases','(b)Dissociation increases','(c)Dissociation does not change','(d)None of these','1','Chemical Equilibrium-Final','2','Answer =  (a)\\(PC{l_5}(g)\\) \\(\\rightleftharpoons{}\\)\\(PC{l_3}(g) + C{l_2}(g)\\)For this reaction \\(\\Delta n = 2 - 1 = 1\\)Value of \\(\\Delta n\\) is positive so the dissociation of \\(PC{l_5}\\) takeforward by decrease in pressure & by increase in pressure the dissociation of\\(PC{l_5}\\) decrease.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','If equilibrium constants of reaction, \\({N_2} + {O_2}\\)\\(\\rightleftharpoons{}\\)\\(2NO\\) is \\({K_1}\\)and \\(\\frac{1}{2}{N_2} +\\frac{1}{2}{O_2}\\)\\(\\rightleftharpoons{}\\) \\(NO\\) is \\({K_2}\\), then','(a)\\({K_1} = {K_2}\\)','(b)\\({K_2} = \\sqrt {{K_1}} \\)','(c)\\({K_1} = 2{K_2}\\)','(d)\\({K_1} = \\frac{1}{2}{K_2}\\)','2','Chemical Equilibrium-Final','2','Answer =  (b)\\({N_2} + {O_2}\\)   \\(\\rightleftharpoons{}\\)  \\(2NO\\)\\(\\ldots{} \\)..(i)\\(\\frac{1}{2}{N_2} +\\frac{1}{2}{O_2}\\)\\(\\rightleftharpoons{}\\)\\(NO\\)\\(\\ldots{} \\)\\(\\ldots{} \\)(ii)For equation number (i)\\({K_1} = \\frac{{{{[NO]}^2}}}{{[{N_2}]\\,[{O_2}]}}\\)\\(\\ldots{} \\).. (iii)For equation number (ii)\\({K_2} = \\frac{{[NO]}}{{{{[{N_2}]}^{1/2}}{{[{O_2}]}^{1/2}}}}\\)\\(\\ldots{} \\)... (iv)From equation (iii) & (iv) it is clear that\\({K_2} = {({K_1})^{1/2}} = \\sqrt {{K_1}} \\);  Hence, \\({K_2} = \\sqrt {{K_1}} \\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','For the following reaction in gaseous phase  \\(CO + \\frac{1}{2}{O_2} \\toC{O_2}\\); \\({K_p}/{K_c}\\) is','(a)\\({(RT)^{1/2}}\\)','(b)\\({(RT)^{ - 1/2}}\\)','(c)\\((RT)\\)','(d)\\({(RT)^{ - 1}}\\)','2','Chemical Equilibrium-Final','2','Answer =  (b)\\({K_p} = {K_c}{[RT]^{\\Delta {n_g}}}\\)\\(\\Delta {n_g} = 1 - 1.5 =  - 0.5\\)\\({K_p} = {K_c}{[RT]^{ - 1/2}}\\)\\(\\therefore \\,\\,\\frac{{{K_p}}}{{{K_c}}} = {[RT]^{ - 1/2}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','For the reaction \\({N_{2(g)}} + {O_{2(g)}}\\)\\(\\rightleftharpoons{}\\)\\(2N{O_{(g)}}\\), the value of \\({K_c}\\) at \\({o}C\\)is 0.1. When the equilibrium concentrations of both the reactants is 0.5 mol,what is the value of \\({K_p}\\) at the same temperature ','(a)4','(b)27','(c)4/27','(d)1/27','','Chemical Equilibrium-Final','2','Answer = (b)\\({N_{2(g)}} + {O_{2(g)}}\\) \\(\\rightleftharpoons{}\\)\\(2N{O_{(g)}}\\)\\({K_c} = 0.1,\\,{K_p} = {K_c}{(RT)^{\\Delta n}}\\)\\(\\Delta n = 0,\\,{K_p} = {K_c} = 0.1\\)',CURRENT_TIMESTAMP);
