INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The standard state gibbs free energy change for the given isomerizationreaction cis-2-pentene \\(\\rightleftharpoons{}\\) trans -2 -pentene is \\( -3.67\\,kJ/mol\\) at \\(\\). If more trans-2-pentene is added to the reactionvessel, then','(a)More cis -2-pentene is formed','(b)Equilibrium is shifted in the forward direction','(c)Equilibrium remains unaffected','(d)Additional trans-2-pentene is formed','1','Chemical Equilibrium-Final','2','Answer =  (a)Equilibrium shifts backward by Le-chatelier\\'s principle.',CURRENT_TIMESTAMP);
