INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Air at sea level is dense. This is a practical application of','(a)Boyle\\'s law','(b)Charle\\'s law','(c)Avogadro\\'s law','(d)Dalton\\'s law','1','Gaseous State-Final','2','Answer =  (a) At sea level, because of compression by air above the proximal layer ofair, pressure increases hence volume decreases i.e. density increases. It isBoyle\\'s law.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','If 20\\(c{m^3}\\) gas at 1 atm. is expanded to 50 \\(c{m^3}\\) at constant T,then what is the final pressure','(a)\\(20 \\times \\frac{1}{{50}}\\)','(b)\\(50 \\times \\frac{1}{{20}}\\)','(c)\\(1 \\times \\frac{1}{{20}} \\times 50\\)','(d)None of these','1','Gaseous State-Final','2','Answer =  (a)At constant \\(T,{P_1}{V_1} = {P_2}{V_2}\\)\\(1 \\times 20 = {P_2} \\times 50\\)\\({P_2} = \\frac{{20}}{{50}} \\times 1;\\)',CURRENT_TIMESTAMP);
