INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Densities of two gases are in the ratio 1 : 2 and their temperatures are inthe ratio 2 : 1, then the ratio of their respective pressures is','(a)1 : 1','(b)1 : 2','(c)2 : 1','(d)4 : 1','1','Gaseous State-Final','2','Answer =  (a) \\(\\frac{{{d_1}}}{{{d_2}}} = \\frac{1}{2}\\), \\(\\frac{{{T_1}}}{{{T_2}}} =\\frac{2}{1}\\frac{{{P_1}}}{{{P_2}}} = \\frac{{{V_2}}}{{{V_1}}} \\times\\frac{{{T_1}}}{{{T_2}}} = \\frac{{{T_1}.{d_1}}}{{{T_2}.{d_2}}}\\)\\(\\frac{{{P_1}}}{{{P_2}}} = \\frac{2}{1}.\\frac{1}{2} = \\frac{1}{1}\\)',CURRENT_TIMESTAMP);
