INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A 10 g of a gas at atmospheric pressure is cooled from \\({o}C\\) to\\({0^o}C\\) keeping the volume constant, its pressure would become','(a)1/2 atm','(b)1/273 atm','(c)2 atm','(d)273 atm','1','Gaseous State-Final','2','Answer =  (a) \\({T_1} = {Answer = o}C = Answer = + {Answer = o}K = {Answer = o}K\\)\\({T_2} = {0^o}C = Answer = + {0^o}C = {Answer = o}K\\)\\({P_1} = 1\\)Ac\\({P_2} = ?;\\)cording to Gay-Lussac\\'s law\\(\\frac{{{P_1}}}{{{T_1}}} = \\frac{{{P_2}}}{{{T_2}}}\\) \\(\\therefore \\) \\({P_2} =\\frac{{{P_1}{T_2}}}{{{T_1}}} = \\frac{{1 \\times {{Answer = ^o}K}}{{{{Answer = ^o}K}}\\) atm; \\(\\frac{1}{2}\\) atm.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Pressure remaining the same, the volume of a given mass of an ideal gasincreases for every degree centigrade rise in temperature by definite fraction ofits volume at','(a)\\({0^o}C\\)','(b)Its critical temperature','(c)Absolute zero','(d)Its Boyle temperature','1','Gaseous State-Final','2','Answer =  (a)\\({V_t} = {V_o}(1 + {\\alpha _v}t)\\)\\(({V_2} - {V_1}) = \\Delta V = {V_o}\\alpha ({t_2} - {t_1})\\)if \\({t_2} - {t_1} = {1^o}\\)then \\(\\Delta V = \\alpha {V_o}\\)For every \\({1^{\\circ}}C\\)increase in temperature, the volume of a givenmass of an ideal gas increases by a definite fraction \\(\\frac{1}{{',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A certain sample of gas has a volume of 0.2 litre measured at 1 atm.pressure and \\({0^o}C\\). At the same pressure but at \\({o}C\\), its volumewill be','(a)0.4 litres(b)0.8 litres(c)27.8 litres(d)55.6 litres530.400 \\(c{m^3}\\) of oxygen at\\({27^o}C\\)were cooled to \\( - {3^o}C\\) withoutchange in pressure. The contraction in volume will be','','(b)0.8 litres(c)27.8 litres(d)55.6 litres530.400 \\(c{m^3}\\) of oxygen at\\({27^o}C\\)were cooled to \\( - {3^o}C\\) withoutchange in pressure. The contraction in volume will be(a)40 \\(c{m^3}\\)','(b)30 \\(c{m^3}\\)(c)44.4 \\(c{m^3}\\)(d)360 \\(c{m^3}\\)','1','Gaseous State-Final','2','Answer =  (a) \\(\\frac{{{V_1}}}{{{V_2}}} = \\frac{{{T_1}}}{{{T_2}}}{V_2} =\\frac{{{T_2}}}{{{T_1}}}{V_1}\\) \\( = \\frac{{{{Answer = ^o}K}}{{{{Answer = ^o}K}} \\times 0.2L= 0.4L.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The pressure p of a gas is plotted against its absolute temperature T fortwo different constant volumes, \\({V_1}\\) and \\({V_2}\\). When \\({V_1} > {V_2}\\),the','(a)Curves have the same slope and do not intersect','(b)Curves must intersect at some point other than \\(T = 0\\)','(c)Curve for \\({V_2}\\) has a greater slope than that for\\({V_1}\\)','(d)Curve for\\({V_1}\\)has a greater slope than that for\\({V_2}\\)','3','Gaseous State-Final','2','Answer =  (c)At constant volumes\\(P \\propto T\\)P = constant T;     PV = nRT \\(\\therefore P = \\frac{{nR}}{V}T\\)slope = \\(m = \\frac{{nR}}{V}{V_2} < {V_1}\\)\\(\\frac{{{m_1}}}{{{m_2}}} = \\frac{{{V_2}}}{{{V_1}}}\\therefore {m_1} < {m_2}\\) iscurve for V\\(_{2}\\) has a greater slope than for V\\(_{1}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Two closed vessels of equal volume containing air at pressure \\({P_1}\\) andtemperature \\({T_1}\\) are connected to each other through a narrow tube. If thetemperature in one of the vessels is now maintained at \\({T_1}\\) and that in theother at \\({T_2}\\), what will be the pressure in the vessels','(a)\\(\\frac{{2{P_1}{T_1}}}{{{T_1} + {T_2}}}\\)(b)\\(\\frac{{{T_1}}}{{2{P_1}{T_2}}}\\)(c)\\(\\frac{{2{P_1}{T_2}}}{{{T_1} + {T_2}}}\\)(d)\\(\\frac{{2{p_1}}}{{{T_1} +{T_2}}}\\)533."One gram molecule of a gas at N.T.P. occupies 22.4 litres." This fact wasderived from','','(b)\\(\\frac{{{T_1}}}{{2{P_1}{T_2}}}\\)(c)\\(\\frac{{2{P_1}{T_2}}}{{{T_1} + {T_2}}}\\)(d)\\(\\frac{{2{p_1}}}{{{T_1} +{T_2}}}\\)533."One gram molecule of a gas at N.T.P. occupies 22.4 litres." This fact wasderived from(a)Dalton\\'s theory','(b)Avogadro\\'s hypothesis(c)Berzelius hypothesis(d)Law of gaseous volume','3','Gaseous State-Final','2','Answer =  (c)\\(\\frac{{{P_1}}}{{{T_1}}} + \\frac{{{P_1}}}{{{T_1}}} = \\frac{P}{{{T_1}}}+ \\frac{P}{{{T_2}}}\\)\\(\\frac{{2{P_1}}}{{{T_1}}} = P\\left( {\\frac{{{T_1} + {T_2}}}{{{T_1}{T_2}}}}\\right)\\)\\(\\therefore P = \\frac{{2{P_1}({T_1}{T_2})}}{{{T_1}({T_1} + {T_2})}} =\\frac{{2{P_1}{T_2}}}{{{T_1} + {T_2}}};\\)',CURRENT_TIMESTAMP);
