INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','At \\({0^o}C\\) and one atm pressure, a gas occupies cc. If the pressureis increased to one and a half-time and temperature is increased by one-third ofabsolute temperature, then final volume of the gas will be','(a)80 cc','(b)88.9 cc','(c)66.7 cc','(d)100 cc','2','Gaseous State-Final','2','Answer =  (b) \\({V_2} = \\frac{{{P_{ 1}}{V_1}{T_2}}}{{{P_2}{T_1}}}\\)\\(\\Rightarrow{}\\)  \\({P_1} = P\\); \\({T_1} ={Answer = o}K\\)\\({P_2} = \\frac{3}{2}P\\)\\({V_2} = \\frac{{2P}}{{3P}} \\times \\frac{4}{3} \\times \\frac{{Answer = }{{Answer = } \\timesAnswer = c = \\frac{{Answer = }{9}cc = 88.Answer = c\\)\\({T_2} = {T_1} + \\frac{{{T_1}}}{3} = \\frac{4}{3} \\times {Answer = o}K;\\)= 88.9 cc',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Correct gas equation is','(a)\\(\\frac{{{V_1}{T_2}}}{{{P_1}}} =\\frac{{{V_2}{T_1}}}{{{P_2}}}\\)','(b)\\(\\frac{{{P_1}{V_1}}}{{{P_2}{V_2}}} =\\frac{{{T_1}}}{{{T_2}}}\\)','(c)\\(\\frac{{{P_1}{T_2}}}{{{V_1}}} =\\frac{{{P_2}{V_2}}}{{{T_2}}}\\)','(d)\\(\\frac{{{V_1}{V_2}}}{{{T_1}{T_2}}} ={P_1}{P_2}\\)','','Gaseous State-Final','2','Answer = (b)\\(\\frac{{{P_1}{V_1}}}{{{T_1}}} = \\frac{{{P_2}{V_2}}}{T}\\therefore\\frac{{{P_1}{V_1}}}{{{P_2}{V_2}}} = \\frac{{{T_1}}}{{{T_2}}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Two separate bulbs contain ideal gases A and B. The density of gas A istwice that of gas B. The molecular mass of A is half that of gas B. The two gasesare at the same temperature. The ratio of the pressure of A to that of gas B is','(a)22.4 litres','(b)33.6 litres','(c)448 litres','(d)44800 ml','3','Gaseous State-Final','2','Answer =  (c)\\({d_a} = 2{d_b}\\) ; \\(2{M_a} = {M_b}\\)\\(PV = nRT = \\frac{m}{M}RT\\)\\(\\frac{{{P_a}}}{{{P_b}}} = \\frac{{{d_a}}}{{{d_b}}}\\frac{{{M_b}}}{{{M_a}}} =\\frac{{2{d_b}}}{{{d_b}}} \\times \\frac{{2{M_a}}}{{{M_a}}} = 4\\)\\(P = \\frac{m}{V}.\\frac{{RT}}{M} = \\frac{{dRT}}{M};\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Pure hydrogen sulphide is stored in a tank of litre capacity at\\({20^o}C\\) and 2 atm pressure. The mass of the gas will be','(a)34 g','(b)340 g','(c)282.4 g','(d)28.24 g','3','Gaseous State-Final','2','Answer =  (c) \\(n = \\frac{{PV}}{{RT}} = \\frac{m}{M}\\)\\(m = \\frac{{MPV}}{{RT}} = \\frac{{34 \\times 2 \\times Answer = }{{0.Answer = \\times Answer = } =',CURRENT_TIMESTAMP);
