INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','One litre of a gas weighs 2 g at ','','','','','1','Gaseous State-Final','2','Answer =  (a) \\(\\frac{{{P_1}{V_1}}}{{{n_1}{T_1}}} =\\frac{{{P_2}{V_2}}}{{{n_2}{T_2}}}\\therefore {T_2} =\\frac{{{P_2}}}{{{P_1}}}\\frac{{{V_2}}}{{{V_1}}}{T_1}\\frac{{{m_1}}}{{{m_2}}}\\)\\( = \\frac{{0.75}}{1} \\times \\frac{1}{1} \\times \\frac{2}{1} \\times {Answer = o}K\\)\\(= {Answer = o}K\\)',CURRENT_TIMESTAMP);
