INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A wheather balloon filled with hydrogen at 1 atm and \\({27^o}C\\) has volumeequal to 0 litres. On ascending it reaches a place where the temperature is\\( - {23^o}C\\) and pressure is 0.5 atm. The volume of the balloon is','(a)24000 litres','(b)20000 litres','(c)10000 litres','(d)12000 litres','2','Gaseous State-Final','2','Answer =  (b)\\({V_2} = \\frac{{{P_1}}}{{{P_2}}}\\frac{{{T_2}}}{{{T_1}}}.{V_1} =\\frac{1}{{0.5}} \\times \\frac{{Answer = }{{Answer = } \\times Answer = 0lit. = Answer = 0lit.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The density of a gas at \\({27^o}C\\) and 1 atm is d. Pressure remainingconstant at which of the following temperatures will its density become 0.75 d','(a)\\({20^o}C\\)','(b)\\({30^o}C\\)','(c)','','3','Gaseous State-Final','2','Answer =  (c)At constant pressure\\(V \\propto nT \\propto \\frac{m}{M}T\\)\\(\\frac{{{V_1}}}{{{V_2}}} = \\frac{{{m_1}{T_1}}}{{{m_2}{T_2}}}\\therefore\\frac{{{T_1}}}{{{T_2}}} = \\frac{{{V_1}}}{{{m_1}}} \\times \\frac{{{m_2}}}{{{V_2}}}= \\frac{{{d_2}}}{{{d_1}}}\\)\\(\\Rightarrow \\frac{{{{Answer = ^o}K}}{{{T_2}}} = \\frac{{0.75d}}{d}\\)\\({T_2} = \\frac{{Answer = }{{0.75}} = {Answer = o}K\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A sample of gas occupies ml at \\({27^o}C\\) and mm pressure. When itsvolume is changed to 80 ml at mm pressure, the temperature of the gas will be','(a)\\({21.6^o}C\\)','(b)\\({240^o}C\\)','(c)\\( - {33^o}C\\)','(d)\\({89.5^o}C\\)','3','Gaseous State-Final','2','Answer =  (c) \\(\\frac{{{P_1}{V_1}}}{{{T_1}}} = \\frac{{{P_2}{V_2}}}{{{T_2}}}\\therefore{T_2} = \\frac{{{P_2}{V_2}}}{{{P_1}{V_1}}} \\times {T_1} = \\frac{{Answer = }{{Answer = }\\times \\frac{{80}}{{Answer = } \\times Answer = \)\\( = {Answer = o}K\\)\\(=  - {33^o}C\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Dalton\\'s law of partial pressure will not apply to which of the followingmixture of gases','(a)\\({H_2}\\) and \\(S{O_2}\\)','(b)\\({H_2}\\) and \\(C{l_2}\\)','(c)\\({H_2}\\) and \\(C{O_2}\\)','(d)\\({H_2}\\) and \\(S{O_2}\\)','2','Gaseous State-Final','2','Answer =  (b) Because \\({H_2}\\) & \\(C{l_2}\\) gases may react with each other toproduce \\(HCl\\) gas hence Dalton\\'s law is not applicable.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following mixtures of gases does not obey Dalton\\'s law ofpartial pressure','(a)\\({O_2}\\) and \\(C{O_2}\\)','(b)\\({N_2}\\) and \\({O_2}\\)','(c)\\(C{l_2}\\) and \\({O_2}\\)','(d)\\(N{H_3}\\) and HCl','4','Gaseous State-Final','2','Answer =  (d)Because \\(HCl\\) & \\(N{H_3}\\) gases may react to produce \\(N{H_4}Cl\\)gas. Dalton\\'s Law is applicable for non reacting gas mixtures.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','To which of the following gaseous mixtures is Dalton\\'s law not applicable','(a)\\(Ne + He + S{O_2}\\)','(b)\\(N{H_3} + HCl + HBr\\)','(c)\\({O_2} + {N_2} + C{O_2}\\)','(d)\\({N_2} + {H_2} + {O_2}\\)','2','Gaseous State-Final','2','Answer =  (b)\\(N{H_3}\\) and \\(HCl\\) & \\(HBr\\) is a reacting gas mixture to produce\\(N{H_4}Cl \\,\\, \\& \\,\\, N{H_4}Br\\) so Dalton\\'s law is not applicable.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Equal amounts of two gases of molecular weight 4 and 40 are mixed. Thepressure of the mixture is 1.1 atm. The partial pressure of the light gas in thismixture is','(a)0.55 atm','(b)0.11 atm','(c)1 atm','(d)0.12 atm','3','Gaseous State-Final','2','Answer =  (c)No. of moles of lighter gas \\( = \\frac{m}{4}\\)No. of moles of heavier gas \\( = \\frac{m}{{40}}\\)Total no. of moles \\( = \\frac{m}{4} + \\frac{m}{{40}} = \\frac{{11m}}{{40}}\\)Mole fraction of lighter gas \\( = \\frac{{\\frac{m}{4}}}{{\\frac{{11m}}{{40}}}} =\\frac{{10}}{{11}}\\)Partial pressure due to lighter gas \\( = {P_o} \\times \\frac{{10}}{{11}}\\)\\(= 1.1 \\times \\frac{{10}}{{11}} = 1atm.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following gas will have highest rate of diffusion','(a)\\(N{H_3}\\)','(b)\\({N_2}\\)','(c)\\(C{O_2}\\)','(d)\\({O_2}\\)','1','Gaseous State-Final','2','Answer =  (a) m. wt. of \\(N{H_3} = 17\\); m.wt. of \\({N_2} = 28\\)m.wt. of \\(C{O_2} = 44\\); m.wt. of \\({O_2} = 32\\)beacuse \\(N{H_3}\\)is lightest gas out of these gases\\(\\left[ {r \\propto \\frac{1}{{\\sqrt {{\\rm{Molecular}}\\,{\\rm{Weight}}} }}} \\right]\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The ratio of the rate of diffusion of a given element to that of helium is1.4. The molecular weight of the element is','(a)2','(b)4','(c)8','(d)16','1','Gaseous State-Final','2','Answer =  (a) \\(\\frac{{{r_g}}}{{{r_{He}}}} = \\sqrt {\\frac{{{M_{He}}}}{{{M_g}}}}\\therefore {M_g} = {M_{He}}.\\frac{{{r^2}_{He}}}{{{r^2}_g}} =\\frac{4}{{{{(1.4)}^2}}} = \\frac{4}{{1.96}} = 2\\)\\(\\left[ {Note:1.4 = \\sqrt 2 } \\right]\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A gas diffuse 1/5 times as fast as hydrogen. Its molecular weight is','(a)50','(b)25','(c)\\(25\\sqrt 2 \\)','(d)\\(50\\sqrt 2 \\)','1','Gaseous State-Final','2','Answer =  (a)\\({r_g} = \\frac{1}{5}.{r_{{H_2}}}\\)\\(\\frac{{{M_g}}}{{{M_{{H_2}}}}} = {\\left[ {\\frac{{{r_{{H_2}}}}}{{{r_g}}}}\\right]^2} = {(5)^2} = 25\\)\\({M_g} = 2 \\times 25 = 50;\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The molecular weight of a gas which diffuses through a porous plug at 1/6thof the speed of hydrogen under identical conditions is','(a)27','(b)72','(c)36','(d)48','2','Gaseous State-Final','2','Answer =  (b)\\({r_g} = \\frac{1}{6}{r_{{H_2}}}\\);  \\({M_g} = {M_{{H_2}}}.{\\left[{\\frac{{{r_{{H_2}}}}}{{{r_g}}}} \\right]^2} = 2 \\times {6^2} = 2 \\times 36 = 72\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Molecular weight of a gas that diffuses twice as rapidly as the gas withmolecular weight 64 is','(a)16','(b)8','(c)64','(d)6.4','1','Gaseous State-Final','2','Answer =  (a) \\({M_1} = 64\\); \\({r_2} = 2{r_1}\\)\\({M_2} = {M_1}{\\left[ {\\frac{{{r_1}}}{{{r_2}}}} \\right]^2} = 64 \\times\\frac{1}{4} = 16\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The densities of hydrogen and oxygen are 0.09 and 1.44 g \\({L^{ - 1}}\\). Ifthe rate of diffusion of hydrogen is 1 then that of oxygen in the same units willbe','(a)4','(b)1/4','(c)16','(d)1/16','2','Gaseous State-Final','2','Answer =  (b)\\({r_O} = {r_H}\\sqrt {\\frac{{{d_H}}}{{{d_O}}}}  =1\\sqrt[\\,]{{\\frac{{0.09}}{{1.44}}}} = \\sqrt {\\frac{1}{{16}}}  = \\frac{1}{4}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','If rate of diffusion of A is 5 times that of B, what will be the densityratio of A and B','(a)1/25','(b)1/5','(c)25','(d)4','','Gaseous State-Final','2','Answer = (a) \\({r_a} = 5{r_b}\\); \\(\\frac{{{d_a}}}{{{d_b}}} = {\\left[{\\frac{{{r_b}}}{{{r_a}}}} \\right]^2} = {\\left( {\\frac{1}{5}} \\right)^2} =\\frac{1}{{25}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The densities of two gases are in the ratio of 1 : 16. The ratio of theirrates of diffusion is','(a)16 : 1','(b)4 : 1','(c)1 : 4','(d)1 : 16','2','Gaseous State-Final','2','Answer =  (b) \\(\\frac{{{d_1}}}{{{d_2}}} = \\frac{1}{{16}}\\) ;\\(\\frac{{{r_1}}}{{{r_2}}} = \\sqrt {\\frac{{{d_2}}}{{{d_1}}}}  = \\sqrt {16}  =\\frac{4}{1}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','At constant volume and temperature conditions, the rate of diffusion\\({D_A}\\) and \\({D_B}\\) of gases A and B having densities \\({\\rho _A}\\) and\\({\\rho _B}\\) are related by the expression ','(a)\\({D_A} = {\\left[ {{D_B} \\cdot \\frac{{{\\rho _A}}}{{{\\rho _B}}}}\\right]^{1/2}}\\)','(b)\\({D_A} = {\\left[ {{D_B} \\cdot \\frac{{{\\rho _A}}}{{{\\rho_B}}}} \\right]^{1/2}}\\)','(c)\\({D_A} = {D_B}{\\left( {\\frac{{{\\rho _A}}}{{{\\rho _B}}}}\\right)^{1/2}}\\)','(d)\\({D_A} = {D_B}{\\left( {\\frac{{{\\rho _B}}}{{{\\rho _A}}}}\\right)^{1/2}}\\)','4','Gaseous State-Final','2','Answer =  (d) \\(\\frac{{{D_A}}}{{{D_B}}} = \\sqrt {\\frac{{{\\rho _B}}}{{{\\rho _A}}}}  ={\\left[ {\\frac{{{\\rho _B}}}{{{\\rho _A}}}} \\right]^{\\frac{1}{2}}}\\);  \\(\\therefore{D_A} = {D_B}{\\left( {\\frac{{{\\rho _B}}}{{{\\rho _A}}}} \\right)^{\\frac{1}{2}}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Atmolysis is a process of','(a)Atomising gas molecules','(b)The breaking of atoms to sub-atomic particles','(c)Separation of gases from their gaseous mixture','(d)Changing of liquids to their vapour state','3','Gaseous State-Final','2','Answer =  (c)Gases may be separated by this process because of different rates ofdiffusion due to difference in their densities.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A bottle of ammonia and a bottle of dry hydrogen chloride connected througha long tube are opened simultaneously at both ends, the white ammonium chloridering first formed will be','(a)At the centre of the tube','(b)Near the hydrogen chloride bottle','(c)Near the ammonia bottle','(d)Throughout the length of the tube','2','Gaseous State-Final','2','Answer =  (b) \\(N{H_4}Cl\\) ring will first formed near the \\(HCl\\) bottle becauserate of diffusion of \\(N{H_3}\\)is more than that of \\(HCl\\) because\\({M_{N{H_3}}}:{M_{HCl}} = 17:36.5)\\). SO \\(N{H_3}\\)will reach first to the\\(HCl\\)bottle & will react there with \\(HCl\\)to form \\(N{H_4}Cl\\) ring',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following pairs will diffuse at the same rate through a porousplug','(a)CO, \\(N{O_2}\\)','(b)\\(N{O_2},\\,C{O_2}\\)','(c)\\(N{H_3},\\,P{H_3}\\)','(d)NO\\({C_2}{H_6}\\)','4','Gaseous State-Final','2','Answer =  (d)Because both \\(NO\\)and \\({C_2}{H_6}\\) have same molecular weights \\(\\left[{{M_{NO}} = {M_{{C_2}{H_6}}} = 30} \\right]\\) and rate of diffusion \\( \\propto\\)molecular weight.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A gas diffuse at a rate which is twice that of another gas B. The ratio ofmolecular weights of A to B is','(a)1.0','(b)0.75','(c)0.50','(d)0.25','4','Gaseous State-Final','2','Answer =  (d)\\(\\frac{{{M_A}}}{{{M_B}}} = {\\left( {\\frac{{{r_B}}}{{{r_A}}}}\\right)^2}{r_A} = 2{r_B}\\)  \\(\\therefore \\frac{{{r_B}}}{{{r_A}}} = \\frac{1}{2}\\)\\( = \\frac{1}{{{{(2)}^2}}}\\) \\( = \\frac{1}{4} = .25\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Two grams of hydrogen diffuse from a container in 10 minutes. How many gramsof oxygen would diffuse through the same container in the same time under similarconditions','(a)0.5 g','(b)4 g','(c)6 g','(d)8 g','','Gaseous State-Final','2','Answer = (a) \\({r_H} = \\frac{{2gm}}{{10\\min }}\\) if \\({r_O} = \\frac{{xgm}}{{10\\min}}\\) \\({r_O} = {r_H}\\sqrt {\\frac{{{M_{{H_2}}}}}{{{M_{{O_2}}}}}}  =\\frac{2}{{10}}\\sqrt {\\frac{2}{{32}}} \\)\\(\\frac{x}{{10}} = \\frac{2}{{10 \\times 4}} = \\frac{1}{2}gm. = .5gm\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The rate of diffusion of methane at a given temperature is twice that of X.The molecular weight of X is','(a)64.0','(b)32.0','(c)40.0','(d)80.0','','Gaseous State-Final','2','Answer = (a) \\({r_{C{H_4}}} = 2{r_g}\\)\\({M_g} = {M_{C{H_4}}}{\\left( {\\frac{{{r_{C{H_4}}}}}{{{r_g}}}} \\right)^2} = 16\\times {2^2} = 64\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','X ml of \\({H_2}\\) gas effuses through a hole in a container in 5 seconds.The time taken for the effusion of the same volume of the gas specified belowunder identical condition is','(a)10 seconds : He','(b)20 seconds : \\({O_2}\\)','(c)25 seconds : CO','(d)55 seconds : \\(C{O_2}\\)','','Gaseous State-Final','2','Answer = (b) \\(r \\propto \\frac{1}{{\\sqrt M }}\\)\\(\\,\\,r =\\frac{{Volume\\,\\,effused}}{{time\\,\\,taken}} = \\frac{V}{t}\\)\\(\\frac{V}{t} \\propto \\frac{1}{{\\sqrt M }}\\) \\(\\therefore{}\\)for same volumes (Vconstant)\\(t \\propto \\sqrt M \\,\\therefore \\frac{{{t_1}}}{{{t_2}}} = \\sqrt{\\frac{{{M_1}}}{{{M_2}}}}\\)\\({t_{He}} = {t_{{H_2}}}\\sqrt {\\frac{{{M_{He}}}}{{{M_{{H_2}}}}}}  =5\\sqrt[{}]{{\\frac{4}{2}}} = 5\\sqrt 2 s.\\)\\({t_{{O_2}}} = t\\)\\(= 5\\sqrt[{}]{{\\frac{{32}}{2}}} = 20s\\)\\({t_{CO}} = \\)\\(5\\sqrt[{}]{{\\frac{{28}}{2}}} = 5\\sqrt {14}s\\) \\({t_{C{O_2}}} = \\)\\(5\\sqrt[{}]{{\\frac{{44}}{2}}} = 5\\sqrt {22} s;\\)',CURRENT_TIMESTAMP);
