INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','What is the ratio of diffusion rate of oxygen and hydrogen','(a)1 : 4','(b)4 : 1','(c)1 : 8','(d)8 : 1','','Gaseous State-Final','2','Answer = (a)\\({M_1} = 32g\\) for \\({O_2}\\), \\({M_2} = 2g\\) for \\({H_2}\\)\\(\\frac{{{r_1}}}{{{r_2}}} = \\sqrt {\\frac{{{M_2}}}{{{M_1}}}} \\)\\(\\frac{{{r_1}}}{{{r_2}}} = \\sqrt {\\frac{2}{{32}}}  = \\sqrt {\\frac{1}{{16}}}  =\\frac{1}{4};\\)',CURRENT_TIMESTAMP);
