INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Equation of Boyle\\'s law is ','(a)\\(\\frac{{dP}}{p} =  - \\frac{{dV}}{V}\\)','(b)\\(\\frac{{dP}}{P} =  +\\frac{{dV}}{V}\\)','(c)\\(\\frac{{{d^2}P}}{P} =  - \\frac{{dV}}{{dT}}\\)','(d)\\(\\frac{{{d^2}P}}{P} =  +\\frac{{{d^2}V}}{{dT}}\\)','','Gaseous State-Final','2','Answer = (a)Boyle\\'s law \\( - PV\\)= constantOn differentiating the equation,\\(d(PV) = d(C)\\) \\(\\Rightarrow{}\\) \\(PdV + VdP = 0\\)\\(\\Rightarrow{}\\) \\(VdP =  - PdV\\)\\(\\Rightarrow{}\\) \\(\\frac{{dP}}{P} =  -\\frac{{dV}}{V}\\).',CURRENT_TIMESTAMP);
