INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Internal energy and pressure of a gas per unit volume are related as','(a)\\(P = \\frac{2}{3}E\\)','(b)\\(P = \\frac{3}{2}E\\)','(c)\\(P = \\frac{1}{2}E\\)','(d)\\(P = 2E\\)','','Gaseous State-Final','2','Answer = (a) KE \\( = \\frac{3}{2}RT = \\frac{3}{2}PV\\)\\(\\therefore P = \\frac{2}{3}\\frac{E}{V}\\) for unit volume (V = 1) \\(\\Rightarrow{}\\) \\(P = \\frac{2}{3}E\\)',CURRENT_TIMESTAMP);
