INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The root mean square velocity of an ideal gas in a closed container of fixedvolume is increased from \\(5 \\times {10^4}\\,cm\\,{s^{ - 1}}\\) to \\(10 \\times{10^4}\\,cm\\,{s^{ - 1}}\\). Which of the following statement correctly explains howthe change is accomplished','(a)By heating the gas, the temperature is doubled','(b)By heating the gas, the pressure is quadrupled (i.e. made four times)','(c)By heating the gas, the temperature isquadrupled','(d)By heating the gas, the pressure is doubled','','Gaseous State-Final','2','Answer = (b)\\(\\frac{{{U_1}}}{{{U_2}}} = \\sqrt {\\frac{{{T_1}}}{{{T_2}}}}\\)\\(\\therefore \\frac{{{T_1}}}{{{T_2}}} = {\\left( {\\frac{{5 \\times {{10}^4}}}{{10\\times {{10}^4}}}} \\right)^2} = \\frac{1}{4}\\)',CURRENT_TIMESTAMP);
