INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','What is the relationship between the average velocity (v), root mean squarevelocity (u) and most probable velocity ','(a)','(a)\\(\\alpha :v:u::1:1.128:1.224\\)','(b)\\(\\alpha :v:u::1.128:1:1.224\\)','(c)\\(\\alpha :v:u::1.128:1.224:1\\)(d)\\(\\alpha :v:u::1.124:1.228:1\\)','','Gaseous State-Final','2','Answer = (a) \\({V_{av}}:{V_{rms}}\\): V\\(_{most probable }\\) = \\(V:U:\\alpha \\)\\(\\sqrt {\\frac{{8RT}}{{\\pi M}}} :\\sqrt {\\frac{{3RT}}{M}} :\\sqrt {\\frac{{2RT}}{M}}\\)\\(\\alpha :V:U = \\sqrt 2 :\\sqrt {\\frac{8}{\\pi }} :\\sqrt 3 \\)\\(= 1:1.Answer = 1.Answer = \)',CURRENT_TIMESTAMP);
