INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','If some moles of \\({O_2}\\) diffuse in 18 sec and same moles of other gasdiffuse in 45 sec then what is the molecular weight of the unknown gas','(a)\\(\\frac{{{{45}^2}}}{{{{18}^2}}} \\times 32\\)','(b)\\(\\frac{{{{18}^2}}}{{{{45}^2}}}\\times 32\\)','(c)\\(\\frac{{{{18}^2}}}{{{{45}^2} \\times 32}}\\)','(d)\\(\\frac{{{{45}^2}}}{{{{18}^2}\\times 32}}\\)','','Gaseous State-Final','2','Answer = (a)\\({r_{{O_2}}} = \\frac{x}{{18}}mole/\\sec \\) \\(\\Rightarrow{}\\)\\({r_g} =\\frac{x}{{45}}mol/\\sec \\)\\({M_g} = {M_O}_2{\\left( {\\frac{{{r_{{O_2}}}}}{{{r_g}}}} \\right)^2} = 32{\\left({\\frac{x}{{18}} \\times \\frac{{45}}{x}} \\right)^2}\\)\\(= 32 \\times \\frac{{{{45}^2}}}{{{{18}^2}}}\\)',CURRENT_TIMESTAMP);
