INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Purpose of smelting of an ore is','(a)To oxidise it','(b)To reduce it','(c)To remove vaporisable impurities','(d)To obtain an alloy','2','General Principle of Extraction of Metal','2','Answer =  (b)Smelting is a process of reducing metal oxide to metal by means of cokeor \\(CO\\).\\(F{e_2}{O_3} + 3C \\to 2Fe + 3CO\\)\\(F{e_2}{O_3} + 3CO \\to 2Fe + 3C{O_2}\\)',CURRENT_TIMESTAMP);
