INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In the manufacture of iron lime stone added to the blast furnace, thecalcium ion ends in the form of','(a)Slag','(b)Gangue','(c)Calcium metal','(d)\\(CaC{O_3}\\)','1','General Principle of Extraction of Metal','2','Answer =  (a)\\(\\mathop {Si{O_2}}\\limits_{{\\rm{Im}}{\\rm{purity}}}  + \\mathop{CaO}\\limits_{{\\rm{Flux}}}  \\to \\mathop {CaSi{O_3}}\\limits_{{\\rm{Slag}}} \\)',CURRENT_TIMESTAMP);
