INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Calcination is used in metallurgy for removal of','(a)Water and sulphide','(b)Water and \\(C{O_2}\\)','(c)\\(C{O_2}\\) and \\({H_2}S\\) ','(d)\\({H_2}O\\) and \\({H_2}S\\)','2','General Principle of Extraction of Metal','2','Answer =  (b)\\(ZnC{O_3} \\to ZnO + C{O_2}\\)In calcination ore is heated in absence of air in a reverberatory furnace toremove moisture and \\(C{O_2}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','When a metal is to be extracted from its ore, if the gangue associated withthe ore is silica, then','(a)An acidic flux is needed','(b)A basic flux is needed','(c)Both acidic and basic flux are needed','(d)Neither of them is needed','2','General Principle of Extraction of Metal','2','Answer =  (b)\\(\\mathop {Si{O_2}}\\limits_{{\\rm{Acidic}}\\,{\\rm{impurity}}}  + \\mathop{CaO}\\limits_{{\\rm{Basic}}\\,{\\rm{flux}}}  \\to \\mathop{CaSi{O_3}}\\limits_{{\\rm{Slag}}} \\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In the modern blast furnaces, the charge consists of a mixture of','(a)Calcined iron oxides + lime + anthracite coal','(b)Calcined iron oxides + limestone + coke','(c)Hydrated iron oxides + dolomite + coke','(d)Iron pyrites + lime +bituminous coal','2','General Principle of Extraction of Metal','2','Answer =  (b)\\(\\mathop {CaC{O_3}}\\limits_1 \\mathop  + \\limits_: \\mathop{{\\rm{Coke}}}\\limits_4 \\mathop  + \\limits_: \\mathop {{\\rm{Calcined}}}\\limits_8 \\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following ores is subjected to roasting during metallurgicaloperations for getting the metal oxide','(a)Horn silver','(b)Zinc blende','(c)Malachite','(d)Limonite','2','General Principle of Extraction of Metal','2','Answer =  (b)Zinc blende\\((ZnS)\\); \\(2ZnS + 3{O_2}2ZnO + 2S{O_2}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A metal obtained directly by roasting of its sulphide ore is','(a)\\(Cu\\)','(b)\\(Pb\\)','(c)\\(Hg\\)','(d)\\(Zn\\)','','General Principle of Extraction of Metal','2','Answer = (c)When conc. \\(HgS\\) ore is roasted\\(HgS + {O_2}Hg + S{O_2}\\)At this temperature, mercury vaporises and the vapours are condensed to theliquid metal. Mercury so obtained is about 99.7% pure.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In blast furnace, the highest temperature is in','(a)Reduction zone','(b)Slag zone','(c)Fusion zone','(d)Combustion zone','4','General Principle of Extraction of Metal','2','Answer =  (d)\\(\\begin{array}{p{82pt}p{44pt}}Combustion zone   &   Answer =  K\\\\Fusion zone & Answer =  K \\\\Slage zone & Answer =  K \\\\Treduction zone & Answer = K \\\\\\end{array}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The process of roasting of an ore is carried out in the','(a)Absence of air','(b)Presence of air','(c)Limited supply of air','(d)None of these','2','General Principle of Extraction of Metal','2','Answer =  (b)In roasting process, the ore (usually sulphide) alone or mixed withother materials is heated in excess of air.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Flux is used to remove','(a)Acidic impurities(b)Basic impurities(c)All impurities from ores(d)Both ','','(b)Basic impurities(c)All impurities from ores(d)Both (a) and ','(b)','4','General Principle of Extraction of Metal','2','Answer =  (d)Flux is used to fuse non-fusible impurities presents in ore.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','During extraction of \\(Fe\\); slag obtained is ','(a)\\(FeO\\)','(b)\\(FeSi{O_3}\\)','(c)\\(MgSi{O_3}\\)','(d)\\(CaSi{O_3}\\)','4','General Principle of Extraction of Metal','2','Answer =  (d)During extraction of \\(Fe\\) calcium silicate \\((CaSi{O_3})\\) slag isobtained.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The final step for the extraction of copper from copper pyrite in Bessemereconverter involves the reaction','(a)\\(4C{u_2}O + FeS \\to 8Cu + FeS{O_4}\\)','(b)\\(C{u_2}S + 2C{u_2}O \\to 6Cu + S{O_2}\\)','(c)\\(2C{u_2}O + FeS \\to 4Cu + Fe + S{O_2}\\)','(d)\\(C{u_2}S + 2FeO \\to 2Cu + 2FeCO + S{O_2}\\)','2','General Principle of Extraction of Metal','2','Answer =  (b)In Bessemer converter copper sulphide is partially oxidised to cuprousoxide which further reacts with remaining copper sulphide to form copper andsulphur dioxide. \\(C{u_2}S + 2C{u_2}O \\to 6Cu + S{O_2}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Flux is used to remove','(a)Silica','(b)Metal oxide','(c)All impurities from ores','(d)Silica and undesirable metal oxide','','General Principle of Extraction of Metal','2','Answer = (d)Flux is used to remove silica and undesirable metal oxide.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Roasting is done in ','(a)Blast furnace','(b)Open hearth furnace','(c)Electric furnace','(d)None of these','1','General Principle of Extraction of Metal','2','Answer =  (a)Roasting is the process of heating the ore strongly in the presence ofexcess of air. It is generally carried in a reverberatory or blast furnace.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following fluxes is used to remove acidic impurities inmetallurgical process','(a)Silica','(b)Lime stone','(c)Sodium chloride','(d)Sodium carbonate','2','General Principle of Extraction of Metal','2','Answer =  (b)Lime stone which is a flux used to remove acidic impurities inmetallurgical process.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','How is limestone used in Fe extraction','(a)Oxidation of Fe ore','(b)Reduction of Fe ore','(c)Formation of slag','(d)Purification of Feformed','3','General Principle of Extraction of Metal','2','Answer =  (c)Lime stone \\((CaC{O_3})\\) is used for formation of slag in Fe extraction\\(\\mathop {CaC{O_3}}\\limits_{{\\rm{Lime stone}}} CaO + C{O_2} \\uparrow\\)\\(CaO + \\mathop {Si{O_2}}\\limits_{{\\rm{Gangue}}{\\,\\,\\,\\,o{\\rm{r}}{\\rm{impurity}}}} \\mathop {CaSi{O_3}}\\limits_{{\\rm{slag}}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Heating mixture of \\(C{u_2}O\\) and \\(C{u_2}S\\) will give','(a)\\(Cu + S{O_2}\\)','(b)\\(Cu + S{O_3}\\)','(c)\\(CuO + CuS\\)','(d)\\(C{u_2}S{O_3}\\)','','General Principle of Extraction of Metal','2','Answer = (a)\\(C{u_2}S + 2C{u_2}O \\to 6Cu + S{O_2}\\) (Auto-reduction).This reaction occurs in reverberatory furnace to get metallic copper.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Heating of ore in presence of air to remove sulphur impurities is called','(a)Calcination ','(b)Roasting','(c)Smelting ','(d)None of these','','General Principle of Extraction of Metal','2','Answer = (b)Roasting involves heating of the ore either alone or with some othermaterial usually in presence of air below its fusion temperature. In roasting,definite chemical changes like oxidation, chlorination etc., take place\\(S + {O_2} \\to S{O_2}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The important step in the extraction of metal from carbonate ore is','(a)Calcination','(b)Roasting','(c)Electro-reduction','(d)Cupellation','1','General Principle of Extraction of Metal','2','Answer =  (a)Calcination \\(ZnC{O_3} \\to ZnO + C{O_2}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Electrometallurgical process is used to extract','(a)\\(Fe\\) ','(b)\\(Pb\\)','(c)\\(Na\\) ','(d)\\(Ag\\)','','General Principle of Extraction of Metal','2','Answer = (c)Because \\(Na\\) is very reactive and can not be extracted by means of thereduction by C, CO etc. So extracted by electrolysis.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','General method for the extraction of metal from oxide ore is','(a)Carbon reduction','(b)Reduction byaluminium','(c)Reduction by hydrogen','(d)Electrolytic reduction','1','General Principle of Extraction of Metal','2','Answer =  (a)Carbon reduction, \\(F{e_2}{O_3} + 3C \\to 2Fe + 3CO\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Function of the flux added during smelting is','(a)To make ore porous','(b)To remove gangue','(c)To make reduction easier','(d)To precipitate slag','2','General Principle of Extraction of Metal','2','Answer =  (b)\\({\\rm{Flux}} + {\\rm{Gangue}} \\to {\\rm{Slag}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Alumino-thermic process is used for the extraction of metals, whose oxidesare','(a)Fusible','(b)Not easily reduced by carbon','(c)Not easily reduced by hydrogen','(d)Strongly basic','2','General Principle of Extraction of Metal','2','Answer =  (b)Alumino thermite process involves reduction of oxides such as\\(F{e_2}{O_3},\\,\\,M{n_3}{O_4},\\,C{r_2}{O_3}\\) etc. to metals with aluminum.\\(C{r_2}{O_3} + 2Al \\to A{l_2}{O_3} + 2Cr\\)\\(\\Delta H =  - {\\rm{ve}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In blast furnace iron oxide is reduced by','(a)Silica','(b)\\(CO\\)','(c)Carbon','(d)Lime stone','2','General Principle of Extraction of Metal','2','Answer =  (b)\\(F{e_2}{O_3} + 3CO \\to 2Fe + 3C{O_2}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The substance used in the thermite process of reducing metal ores is [MP PET; CPMT , 01]','(a)Aluminium ','(b)Thorium','(c)Heated Pt gauge ','(d)Carbon','1','General Principle of Extraction of Metal','2','Answer =  (a)A mixture of Al powder and metallic oxide\\((C{r_2}{O_3},\\,M{n_3}{O_4}\\,{\\rm{etc)}}\\) is called thermite.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following metals cannot be extracted by carbon reductionprocess ','(a)\\(Pb\\) ','(b)\\(Al\\)','(c)\\(Hg\\)  ','(d)\\(Zn\\)','2','General Principle of Extraction of Metal','2','Answer =  (b)Al is highly electropositive. It can be obtained by electrolyticreduction.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Carbon reduction process is used for the extraction of','(a)\\(Hg\\) ','(b)\\(Zn\\)','(c)\\(Cr\\) ','(d)\\(Fe\\)','4','General Principle of Extraction of Metal','2','Answer =  (d)\\(F{e_2}{O_3} + 3C \\to 3CO + 2Fe\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Chemical reduction is not suitable for converting','(a)Bauxite into aluminium','(b)Cuprite into copper','(c)Haematite into iron','(d)Zinc oxide into zinc','1','General Principle of Extraction of Metal','2','Answer =  (a)Bauxite into aluminium because Al is a strong reducing agent it hasstrong affinity with oxygen than carbon',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which technique is used in the manufacture of aluminium from bauxite','(a)Reduction with magnesium','(b)Reduction with coke','(c)Electrolytic reduction','(d)Reduction with iron','3','General Principle of Extraction of Metal','2','Answer =  (c)Electrolytic reduction Hall and Heroult process.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Iron is obtained on a large scale from \\(F{e_2}{O_3}\\) by','(a)Reduction with \\(Al\\)','(b)Reduction with \\(CO\\)','(c)Reduction with \\({H_2}\\)','(d)Reduction with sodium','2','General Principle of Extraction of Metal','2','Answer =  (b)\\(F{e_2}{O_3} + 3CO \\to 3C{O_2} + 2Fe\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','After partial roasting, the sulphide of copper is reduced by','(a)Reduction by carbon','(b)Electrolysis','(c)Self-reduction ','(d)Cyanide process','','General Principle of Extraction of Metal','2','Answer = (c)Self reduction :- Reduction of oxide ore of a metal by its own sulphide\\(2C{u_2}O + C{u_2}S \\to 6Cu + S{O_2}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In the metallurgical extraction of zinc from \\(ZnO\\) the reducing agent usedis','(a)Carbon monoxide','(b)Sulphur dioxide','(c)Carbon dioxide','(d)Nitric oxide','1','General Principle of Extraction of Metal','2','Answer =  (a)\\(ZnO + CO \\to C{O_2} + Zn\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In order to refine "blister copper" it is melted in a furnace and isstirred with green logs of wood. The purpose is','(a)To expel the dissolved gases in blister copper','(b)To bring the impurities to surface and oxidize them','(c)To increase the carbon content of copper','(d)To reduce the metallic oxide impurities with hydrocarbon gases liberated fromthe wood','4','General Principle of Extraction of Metal','2','Answer =  (d)\\(2C{u_2}S + 3{O_2} \\to 2C{u_2}O + 2S{O_2}\\)\\(3C{u_2}O + \\mathop {C{H_4}}\\limits_{{\\rm{(From green}}{\\rm{logs of wood)}}}  \\to 6Cu + 2{H_2}O + CO\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The metal extracted by electrolysis of its fused salt is','(a)Iron','(b)Lead','(c)Sodium','(d)Copper','3','General Principle of Extraction of Metal','2','Answer =  (c)Sodium \\(\\rightarrow{}\\) Highly reactive metal',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','To obtain chromium from chromic oxide \\((C{r_2}{O_3}),\\) the method used is','(a)Alumino-thermic process','(b)Electrolytic reduction','(c)Carbon reduction','(d)Carbon monoxide reduction','1','General Principle of Extraction of Metal','2','Answer =  (a)\\(C{r_2}{O_3} + 2Al \\to A{l_2}{O_3} + 2Cr\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The substance used in the thermite process of reducing metal ores is','(a)Aluminium','(b)Thorium','(c)Heated platinum gauze','(d)Carbon','1','General Principle of Extraction of Metal','2','Answer =  (a)In thermite process a mixture of aluminium powder and ferricoxide in therate of 1 : 3 is used.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Heating with carbon in absence of air is known as','(a)Reduction','(b)Carbon-reduction','(c)Smelting','(d)Roasting','2','General Principle of Extraction of Metal','2','Answer =  (b)Heating with carbon in absence of air is known as carbon reduction.This is used in Iron metallurgy.\\(F{e_2}{O_3} + CFe\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which method of purification is represented by the equation\\(\\mathop {Ti + 2{I_2}}\\limits_{{\\rm{Impure}}} Ti{I_4}\\mathop {Ti +2{I_2}}\\limits_{{\\rm{Pure}}} \\) ','(a)Cupellation ','(b)Poling','(c)Van Arkel','(d)Zone refining','','General Principle of Extraction of Metal','2','Answer = (c)Van Arkel method Ti and Zn are refined by this method. It is used forobtaining ultra pure metals.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Cupellation process is used in the metallugry of[CPMT ; MP PET ; MP PMT , 02]','(a)Copper','(b)Silver','(c)Aluminium ','(d)Iron','','General Principle of Extraction of Metal','2','Answer = (b)Cupellation method is used when the impure metals contain impurity ofanother metal which forms volatile oxide.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Metals are ','(a)Electropositive ','(b)Electronegative','(c)Acceptor of electrons ','(d)None of these','','General Principle of Extraction of Metal','2','Answer = (a)Metals are electropositive elements because they have tendency to loose\\({e^ - }\\) and forms + ve ions\\(Na \\to N{a^ + } + {e^ - }\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In electrolytic refining, the impure metal is made is used to make','(a)Cathode','(b)Anode','(c)Electrolytic bath','(d)None of these','','General Principle of Extraction of Metal','2','Answer = (b)Impure metal as anode and pure metal as cathode.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Of the following, which cannot be obtained by electrolysis of the aqueoussolution of their salts','(a)\\(Ag\\) ','(b)\\(Mg\\) and \\(Al\\)','(c)\\(Cu\\) ','(d)\\(Cr\\)','','General Principle of Extraction of Metal','2','Answer = (b)Mg and Al can not be obtained by the electrolysis of aqueous solution oftheir salts because instead of metal \\({H_2}\\) gas is liberated at cathode.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Van Arkel method of purification of metals involves converting the metal toa ','(a)Volatile stable compound','(b)Volatile unstable compound','(c)Non volatile stable compound','(d)None of the above','','General Principle of Extraction of Metal','2','Answer = (a)\\(Ti + 2{I_2}\\mathop{Ti{I_4}}\\limits_{{\\rm{Volatile}}{\\rm{Stable}}\\,{\\rm{compound}}} \\mathop {Ti +2{I_2}}\\limits_{{\\rm{Pure}}\\,{\\rm{metal}}} \\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Zone refining is a method to obtain','(a)Very high temperature','(b)Ultra pure Al','(c)Ultra pure metals','(d)Ultra pure oxides','','General Principle of Extraction of Metal','2','Answer = (c)Zone refining is employed for preparing extremely pure metals.It is based on the principle that when a molten solution of the impure metal isallowed to cool the pure metal crystallises out while the impurities remain inthe melt. Ex :  Semiconductors like Si, Ge and Ga are purified by this method.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which one of the following is manufactured by the electrolysis of fusedsodium chloride[CPMT , 83, 91]','(a)\\(NaOH\\) ','(b)\\(NaClO\\)','(c)\\(Na\\) ','(d)\\(NaCl{O_3}\\)','','General Principle of Extraction of Metal','2','Answer = (c)\\(\\mathop {2NaCl}\\limits_{({\\rm{fused}})}  \\to 2N{a^ + } + 2C{l^ - }\\)Anode: \\(2C{l^ - } \\to 2{e^ - } + C{l_2}\\) (oxidation)Cathode: \\(2N{a^ + } + 2{e^ - } \\to 2Na\\)- (reduction)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A metal which is refined by poling is','(a)Sodium','(b)Blister copper','(c)Zinc','(d)Silver','','General Principle of Extraction of Metal','2','Answer = (b)Poling is used for purification of metal which contain their own oxide asimpuritye.g.   \\(C{u_2}O\\) in \\(Cu\\); \\(Sn{O_2}\\) in \\(Sn\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Silver obtained from argentiferrous lead containing lead impurity ispurified by','(a)Distillation','(b)Froth floatation','(c)Cupellation','(d)Treatment of \\(KCN\\)','','General Principle of Extraction of Metal','2','Answer = (c)Cupellation : If metal possess the impurity of another metal which formsvolatile oxide. Then cupellation method is used.',CURRENT_TIMESTAMP);
