INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Gold sol is an electronegative sol. The amount of electrolyte required tocoagulate a certain amount  of gold sol is minimum in the case of ','(a)\\(CaC{l_2}\\)','(b)\\(NaCl\\)','(c)\\(AlC{l_3}\\)','(d)\\(N{a_2}S{O_4}\\)','','Surface Chemistry','2','Answer = (c)Coagulation is governed by Hardy Schulzerule.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','If gold number of A,B,C and D are 0. 0.05, 0.5 and 5 respectively, thenwhich of the following will have the highest protective power  ','(a)A ','(b)B','(c)C ','(d)D','','Surface Chemistry','2','Answer = (a)Protective power\\( \\propto \\frac{1}{{{\\rm{Gold}}\\;{\\rm{number}}}}\\)Gold number of A is least, therefore, it has the highest protective power.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Bredig arc method can not be used to prepare colloidal solution of which ofthe following ','(a)Pt ','(b)Fe','(c)Ag ','(d)Au','','Surface Chemistry','2','Answer = (b)Bredig\\'s arc method is suitable for the preparation of colloidal solutionof metal like gold silver, platinum etc. An arc is struck between the metalelectrode under the surface of water containing some stabilizing agent such as atrace of KOH. However, Fe does not react with alkalies that is why it is notobtained by Bredig\\'s arc method.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Gold number is maximum for the lyophilic sol is','(a)Gelatin ','(b)Haemoglobin','(c)Sodium oleate ','(d)Potato starch','','Surface Chemistry','2','Answer = (d)Gold number shows the protective power of a lyophilic solution. Lesserthe gold number, greater will be the protecting power of that colloid. Gelatin isone of the best protective colloid. Among the given colloids, potato starch hasmaximum gold number.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following is the best protective colloid','(a)Gelatin (Gold No. = 0.005)','(b)Gum arabic  (Gold No. = 0.15)','(c)Egg albumin  (Gold No. = 0.08)','(d)None of these','','Surface Chemistry','2','Answer = (a)Protective power \\( \\propto \\frac{1}{{{\\rm{Gold number }}}}\\)Thus gelatin is the best protective colloid.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The gold number of A, B C and D are 0.04, 0. 10 and 25 respectively.Protective power of A, B, C and D are in order    ','(a)\\(A > B > C > D\\)','(b)\\(B > A > C > D\\)','(c)\\(D > C > B > A\\)','(d)\\(C > A > B > D\\)','','Surface Chemistry','2','Answer = (b)Protective power \\( \\propto \\frac{1}{{{\\rm{Gold number }}}}\\)Hence, the correct order of protective power is \\(B > A > C > D\\).',CURRENT_TIMESTAMP);
