INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Alum is a water purifier because it  ','(a)Coagulates the impurities','(b)Softens hard water','(c)Gives taste','(d)Destroys the pathogenic bacteria','','Surface Chemistry','2','Answer = (a)Alum contains many cations and water has many anionic impurities. Onadding alum coagulates the suspended impurities and make water fit for drinkingpurposes.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','An emulsifier is a substance which ','(a)Stabilises the emulsion','(b)Homogenises the emulsion','(c)Coagulates the emulsion','(d)Accelerates the dispersion of liquid in liquid','1','Surface Chemistry','2','Answer =  (a)For the stabilisation of an emulsion a third component calledemulsifying agent is usually added. The emulsifier forms an interfacial filmbetween suspended particles and the medium.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Muddy water can be purified through coagulation using','(a)Common salt','(b)Alums','(c)Sand','(d)Lime','2','Surface Chemistry','2','Answer =  (b)Alum is added to muddy water so as to destroy the bacteria as well as tocoagulate the suspended impurities.',CURRENT_TIMESTAMP);
